example.code:
using System;
using MailBee;
using MailBee.Mime;
using MailBee.Pop3Mail;

class Sample
{
	static int  traffic;
	static bool downloadingMsgs;

	// We use Pop3.DataReceived event to simulate connection drop during
	// getting message data from the server. We do this through closing
	// the connection after 50000 bytes of message data has been received from the
	// server. This may occur multiple times (for instance, if there is 1MB of mail
	// in the inbox, the connection will be dropped about 20 times).
	// If, however, there is a message larger than 50KB in the inbox, the download process
	// will stop because it would otherwise end up in an endless loop (because this code
	// always disconnects on getting 50-th KB of the data and it would never have received
	// such message completely).
	static void pop_DataReceived(object sender, DataTransferEventArgs e)
	{
		if (downloadingMsgs)
		{
			traffic += e.Data.Length;

			// Increase 50000 to a larger value if you wish to test this code with
			// a mailbox containing large messages (larger than 50KB).
			if (traffic > 50000)
			{
				((Pop3)sender).Log.WriteLine("SIMULATE CONNECTION DROP");
				((Pop3)sender).GetSocket().Close();
			}
		}
	}

	static void Main(string[] args)
	{
		// Initialize Pop3 object and make it automatically load UIDs of all messages on the server.
		Pop3 pop = new Pop3();
		pop.InboxPreloadOptions = Pop3InboxPreloadOptions.Uidl;

		// Enable logging POP3 session into a file. Useful for troubleshooting.
		// Also, this log can be used to track the history of disconnections and
		// reconnections during downloading messages.
		pop.Log.Filename = @"C:\log.txt";
		pop.Log.Enabled = true;
		pop.Log.Clear();

		// Attach to a DataReceived event which will be used for simulating
		// connection failures.
		pop.DataReceived += new DataTransferEventHandler(pop_DataReceived);

		// Will set it to true once either everything has been downloaded
		// or a permanent error has occurred.
		bool finished = false;

		// Remember UID of the last message which had already been downloaded.
		// After reconnecting, we'll download messages with UIDs corresponding to
		// subsequent messages. For the first time, set it to null to indicate no
		// messages had already been downloaded.
		string lastUid = null;

		// We'll collect downloaded messages in this collection.
		MailMessageCollection msgs = new MailMessageCollection();

		// Because we catch exceptions in this code but still want to know
		// which error has occurred in the case of permanent failure, we remember
		// the reason of it.
		MailBeeException permanentException = null;

		// Enable cache of messages downloaded during execution of the last method.
		pop.EnableLastDownloaded = true;

		do
		{
			// Prevent connection drop simulation when not downloading messages.
			downloadingMsgs = false;

			// Connect or reconnect to the server if required (and login/select inbox).
			if (!pop.IsConnected)
			{
				pop.Connect("pop.company.com");
			}
			if (!pop.IsLoggedIn)
			{
				pop.Login("jdoe", "secret");
			}

			traffic = 0;

			try
			{
				// Enable connection drop simulation.
				downloadingMsgs = true;

				int lastMsgNumber = 0;
				if (lastUid != null)
				{
					lastMsgNumber = pop.GetMessageIndexFromUid(lastUid);
				}

				if (lastMsgNumber < pop.InboxMessageCount)
				{
					// Download the remaining messages.
					pop.DownloadEntireMessages(lastMsgNumber + 1, -1);
					msgs.Add(pop.LastDownloadedMessages);
				}

				// Disable connection drop simulation.
				downloadingMsgs = false;

				// All's done.
				finished = true;
			}
			catch (MailBeeException e)
			{
				downloadingMsgs = false;

				if (pop.LastDownloadedMessages == null ||
					pop.LastDownloadedMessages.Count == 0)
				{
					// Haven't got any messages. Assume this is permanent error
					// (otherwise, we could get endless loop).
					finished = true;
					permanentException = e;
				}
				else
				{
					// Error has occurred but some messages have still been
					// downloaded. We'll proceed with getting the rest of messages.
					msgs.Add(pop.LastDownloadedMessages);

					// Get last UID from the value of UID of the last downloaded message.
					lastUid = (string)pop.LastDownloadedMessages[pop.LastDownloadedMessages.Count - 1].UidOnServer;
				}
			}
		} while (!finished);

		// Clear and disable the cache of the last downloaded messages.
		pop.EnableLastDownloaded = false;

		if (pop.IsConnected)
		{
			// Attempt to disconnect (we need to catch MailBeeSocketObjectDisposedException
			// here because we simulated connection drop bypassing MailBee (using Socket.Close()
			// method) and Pop3.IsConnected could remain true). In real world apps, this
			// is not required and Pop3.IsConnected always reflects the actual state of
			// the connection.
			try
			{
				pop.Disconnect();
			}
			catch (MailBeeSocketObjectDisposedException) {}
		}

		// Display UIDs of the downloaded messages.
		foreach (MailMessage msg in msgs)
		{
			Console.Write((string)msg.UidOnServer + " ");
		}
	}
}

member.example:
This sample logs all the warnings and critical errors issued during the POP3 session into the console.
            If the connection succeeds and the POP3 server does support CAPA command, no output will be produced.
            The sample is written for a console application.

            using System;
            using MailBee;
            using MailBee.Pop3Mail;

            class Sample
            {
            	// ErrorOccurred event handler.
            	private static void OnErrorOccurred(object sender, ErrorEventArgs e)
            	{
            		// Check whether the error is critical or not.
            		if (e.IsFinalError)
            		{
            			// Display corresponding MailBeeException.Message.
            			// After OnErrorOccurred handler finishes, MailBee will 
            			// throw the same exception to the application code.
            			Console.WriteLine("CRITICAL ERROR: " + e.Reason.Message);
            		}
            		else
            		{
            			// Display corresponding MailBeeException.Message.
            			// After OnErrorOccurred handler finishes, MailBee will 
            			// try another methods to accomplish the current task.
            			Console.WriteLine("WARNING: " + e.Reason.Message);
            		}
            	}

            	// The actual code.
            	static void Main(string[] args)
            	{
            		Pop3 pop = new Pop3();

            		// Subscribe to the ErrorOccurred event.
            		pop.ErrorOccurred += new ErrorEventHandler(OnErrorOccurred);

            		// Connect to the server.
            		pop.Connect("mail.domain.com");

            		// Request any data which needs CAPA command to be issued 
            		// in order to obtain the required information. The list of supported 
            		// authentication methods can serve this purpose. If CAPA command 
            		// fails (but the server responds, i.e. the connection itself is still valid),
            		// MailBee will not throw any exception to the application code  
            		// (but ErrorOccurred event will still be raised) and will try to issue 
            		// AUTH command to get the list of supported authentication methods.
            		// If AUTH command fails too, it's still not a critical error. SASL 
            		// authentication methods will just not be available.
            		AuthenticationMethods authMethods = pop.GetSupportedAuthMethods();

            		// Close the connection
            		pop.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports Pop3Mail

            Public Class Sample
            	' ErrorOccurred event handler.
            	Private Shared Sub OnErrorOccurred(ByVal sender As Object, ByVal e As ErrorEventArgs)
            		' Check whether the error is critical or not
            		If e.IsFinalError Then
            			' Display corresponding MailBeeException.Message.
            			' After OnErrorOccurred handler finishes, MailBee will 
            			' throw the same exception to the application code.
            			Console.WriteLine("CRITICAL ERROR: " & e.Reason.Message)
            		Else
            			' Display corresponding MailBeeException.Message.
            			' After OnErrorOccurred handler finishes, MailBee will 
            			' try another methods to accomplish the current task.
            			Console.WriteLine("WARNING: " & e.Reason.Message)
            		End If
            	End Sub

            	' The actual code
            	Shared Sub Main(ByVal args As String())
            		Dim pop As New Pop3

            		' Subscribe to the ErrorOccurred event.
            		AddHandler pop.ErrorOccurred, AddressOf OnErrorOccurred

            		' Connect to the server.
            		pop.Connect("mail.domain.com")

            		' Request any data which needs CAPA command to be issued 
            		' in order to obtain the required information. The list of supported 
            		' authentication methods can serve this purpose. If CAPA command 
            		' fails (but the server responds, i.e. the connection itself is still valid),
            		' MailBee will not throw any exception to the application code 
            		' (but ErrorOccurred event will still be raised) and will try to issue 
            		' AUTH command to get the list of supported authentication methods.
            		' If AUTH command fails too, it's still not a critical error. SASL 
            		' authentication methods will just not be available.
            		Dim authMethodrs As AuthenticationMethods = pop.GetSupportedAuthMethods()

            		' Close the connection.
            		pop.Disconnect()
            	End Sub
            End Class

This sample enables logging the POP3 session into a file, and logs only
            those entries which indicate any data traffic between the client (MailBee) and the server.
            Other entries are discarded, and user-defined log entry is added instead.
            The sample is written for a console application.

            using System;
            using MailBee;
            using MailBee.Pop3Mail;

            class Sample
            {
            	// LogNewEntry event handler.
            	private static void OnLogNewEntry(object sender, LogNewEntryEventArgs e)
            	{
            		if ( !(e.NewEntry.MessageType == LogMessageType.Recv ||
            			e.NewEntry.MessageType == LogMessageType.Send) )
            		{
            			// Do not add this entry if it does not indicate any data transfer,
            			// and put our own message instead.
            			e.NewEntry.AddThisEntry = false;
            			((Pop3)sender).Log.WriteLine("UNWANTED MESSAGE REMOVED");

            			// Note: we could produce similar results just by writing new values
            			// into MessageText and MessageComment properties of the original entry.
            			// The code above uses Log.WriteLine method just to show how to add new
            			// entries in the event handler itself. You may comment 2 code lines above
            			// and uncomment 2 code lines below to use the alternate approach.
            			//
            			// e.NewEntry.MessageText = "UNWANTED MESSAGE REMOVED";
            			// e.NewEntry.MessageComment = string.Empty;
            		}
            	}

            	// The actual code
            	static void Main(string[] args)
            	{
            		Pop3 pop = new Pop3();

            		// Enable logging the POP3 session into a file.
            		pop.Log.Enabled = true;
            		pop.Log.Filename = @"C:\Temp\log.txt";
            		pop.Log.Clear();

            		// Subscribe to the LogNewEntry event.
            		pop.LogNewEntry += new LogNewEntryEventHandler(OnLogNewEntry);

            		// Do something which would produce some network traffic.
            		pop.Connect("mail.domain.com");
            		pop.Login("jdoe", "secret");
            		pop.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.Pop3Mail

            Public Class Sample
            	' LogNewEntry event handler.
            	Private Shared Sub OnLogNewEntry(ByVal sender As Object, ByVal e As LogNewEntryEventArgs)
            		If Not (e.NewEntry.MessageType = LogMessageType.Recv Or _
            			e.NewEntry.MessageType = LogMessageType.Send) Then
            			' Do not add this entry if it does not indicate any data transfer,
            			' and put our own message instead.
            			e.NewEntry.AddThisEntry = False
            			sender.Log.WriteLine("UNWANTED MESSAGE REMOVED");

            			' Note: we could produce similar results just by writing new values
            			' into MessageText and MessageComment properties of the original entry.
            			' The code above uses Log.WriteLine method just to show how to add new
            			' entries in the event handler itself. You may comment 2 code lines above
            			' and uncomment 2 code lines below to use the alternate approach.
            			'
            			' e.NewEntry.MessageText = "UNWANTED MESSAGE REMOVED"
            			' e.NewEntry.MessageComment = String.Empty
            		End If
            	End Sub

            	' The actual code.
            	Shared Sub Main(ByVal args As String())
            		Dim pop As New Pop3

            		' Enable logging the POP3 session into a file.
            		pop.Log.Enabled = True
            		pop.Log.Filename = "C:\Temp\log.txt"
            		pop.Log.Clear()

            		' Subscribe to the LogNewEntry event.
            		AddHandler pop.LogNewEntry, AddressOf OnLogNewEntry

            		' Do something which would produce some network traffic.
            		pop.Connect("mail.domain.com")
            		pop.Login("jdoe", "secret")
            		pop.Disconnect()
            	End Sub
            End Class

