This sample demonstrates that  event raises even
            on failures (when  is thrown). The sample connects to the
            POP3 server and then sends invalid data to the server. The server does not
            respond and the network operation times out.

            using System;
            using MailBee;
            using MailBee.Pop3Mail;

            class Sample
            {
            	// Disconnected event handler.
            	private static void OnDisconnected(object sender, DisconnectedEventArgs e)
            	{
            		if (e.IsNormalShutdown)
            		{
            			// QUIT command was sent to POP3 server.
            			Console.WriteLine("Normally disconnected from the server.");
            		}
            		else
            		{
            			// Rough disconnect (due to failure). If any messages have been 
            			// flagged for deletion, they will possibly not be deleted.
            			Console.WriteLine("The connection was terminated.");
            		}
            	}

            	// The actual code.
            	static void Main(string[] args)
            	{
            		Pop3 pop = new Pop3();

            		// Subscribe to Disconnected event.
            		pop.Disconnected += new DisconnectedEventHandler(OnDisconnected);

            		pop.Connect("mail.domain.com");

            		// We do not want to wait too long. Set one second as timeout value.
            		pop.Timeout = 1000;

            		// Simulate connection error by sending a request to which the POP3
            		// server will never respond (due to a lack of "\r\n" in the request data).
            		// This will cause MailBee to terminate the connection and raise 
            		// Disconnected event, and then throw MailBeeException (exceptions are 
            		// enabled by default).
            		pop.ExecuteCustomCommand("NONSENSE", false);

            		// This line will probably never execute (unless the server 
            		// suddenly responds to NONSENSE sequence of bytes).
            		pop.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.Pop3Mail

            Class Sample
            	' Disconnected event handler.
            	Private Shared Sub OnDisconnected(ByVal sender As Object, ByVal e As DisconnectedEventArgs)
            		If e.IsNormalShutdown Then
            			' QUIT command was sent to POP3 server.
            			Console.WriteLine("Normally disconnected from the server.")
            		Else
            			' Rough disconnect (due to failure). If any messages have been 
            			' flagged for deletion, they will possibly not be deleted.
            			Console.WriteLine("The connection was terminated.")
            		End If
            	End Sub

            	' The actual code.
            	Shared Sub Main(ByVal args As String())
            		Dim pop As New Pop3

            		' Subscribe to the LoggedIn event.
            		AddHandler pop.Disconnected, AddressOf OnDisconnected

            		pop.Connect("mail.domain.com")

            		' We do not want to wait too long. Set one second as timeout value.
            		pop.Timeout = 1000

            		' Simulate connection error by sending a request to which the POP3 
            		' server will never respond (due to a lack of "\r\n" in the request data).
            		' This will cause MailBee to terminate the connection and raise 
            		' Disconnected event, and then throw MailBeeException (exceptions are 
            		' enabled by default).
            		pop.ExecuteCustomCommand("NONSENSE", False)

            		' This line will probably never execute (unless the server 
            		' suddenly responds to NONSENSE sequence of bytes).
            		pop.Disconnect()
            	End Sub
            End Class

This console sample demonstrates that  event is raised during executing  method
            when  property is set to  value.

            using System;
            using MailBee;
            using MailBee.Pop3Mail;
            using MailBee.Security;

            class Sample
            {
            	// TlsStarted event handler.
            	private static void OnTlsStarted(object sender, TlsStartedEventArgs e)
            	{
            		// This will happen during Login method execution.
            		Console.WriteLine("TLS/SSL session started.");
            	}

            	// The actual code.
            	static void Main(string[] args)
            	{
            		Pop3 pop = new Pop3();

            		// Subscribe to TlsStarted event.
            		pop.TlsStarted += new TlsStartedEventHandler(OnTlsStarted);

            		// Notify MailBee it should start TLS/SSL session when appropriate.
            		// The connection is made to the regular port so that STLS command
            		// will be used to start TLS/SSL session.
            		pop.SslMode = SslStartupMode.UseStartTls;

            		pop.Connect("mail.domain.com");

            		Console.WriteLine("Connected to the server. Will login now...");

            		// TLS/SSL negotiation will take place here. Thus, user credentials
            		// will be sent to the mail server already under secure TLS/SSL layer.
            		pop.Login("jdoe@domain.com", "secret");

            		Console.WriteLine("Logged in successfully.");

            		pop.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.Pop3Mail
            Imports MailBee.Security

            Public Class Sample
            	' TlsStarted event handler.
            	Private Shared Sub OnTlsStarted(ByVal sender As Object, ByVal e As TlsStartedEventArgs)
            		' This will happen during Login method execution.
            		Console.WriteLine("TLS/SSL session started.")
            	End Sub

            	' The actual code.
            	Shared Sub Main(ByVal args As String())
            		Dim pop As New Pop3

            		' Subscribe to TlsStarted event.
            		AddHandler pop.TlsStarted, AddressOf OnTlsStarted

            		' Notify MailBee it should start TLS/SSL session when appropriate.
            		' The connection is made to the regular port so that STLS command
            		' will be used to start TLS/SSL session.
            		pop.SslMode = SslStartupMode.UseStartTls

            		pop.Connect("mail.domain.com")

            		Console.WriteLine("Connected to the server. Will login now...")

            		' TLS/SSL negotiation will take place here. Thus, user credentials
            		' will be sent to the mail server already under secure TLS/SSL layer.
            		pop.Login("jdoe@domain.com", "secret")

            		Console.WriteLine("Logged in successfully.")

            		pop.Disconnect()
            	End Sub
            End Class

This sample connects to the POP3 server, logs in the user account, and downloads
            the complete mailbox statistics. The sample demonstrates there is a delay between
             event occurence and  method completion,
            which is the amount of time spent on downloading the mailbox statistics after
            completion of the authentication process.

            using System;
            using MailBee;
            using MailBee.Pop3Mail;

            class Sample
            {
            	// LoggedIn event handler.
            	private static void OnLoggedIn(object sender, LoggedInEventArgs e)
            	{
            		Console.WriteLine("Successfully logged in the user account at " +
            			(DateTime.Now - DateTime.Today));
            	}

            	// The actual code.
            	static void Main(string[] args)
            	{
            		Pop3 pop = new Pop3();

            		// Subscribe to the LoggedIn event.
            		pop.LoggedIn += new LoggedInEventHandler(OnLoggedIn);

            		pop.Connect("mail.domain.com");

            		// Set "download the complete mailbox statistics" mode to increase the delay 
            		// between LoggedIn event and Login method completion 
            		pop.InboxPreloadOptions = Pop3InboxPreloadOptions.List | Pop3InboxPreloadOptions.Uidl;

            		// Authenticate the user, log in the mailbox, and download the mailbox statistics.
            		pop.Login("jdoe", "secret");

            		Console.WriteLine("The user account statistics received at " +
            			(DateTime.Now - DateTime.Today));

            		pop.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.Pop3Mail

            Class Sample
            	' LoggedIn event handler.
            	Private Shared Sub OnLoggedIn(ByVal sender As Object, ByVal e As LoggedInEventArgs)
            		Dim DNow As DateTime = DateTime.Now
            		Dim DToday As DateTime = DateTime.Today
            		Console.WriteLine("Successfully logged in the user account at " & _
            			DateDiff(DateInterval.Hour, DateTime.Now, DateTime.Today))
            	End Sub

            	' The actual code.
            	Shared Sub Main(ByVal args As String())
            		Dim pop As New Pop3

            		' Subscribe to the LoggedIn event.
            		AddHandler pop.LoggedIn, AddressOf OnLoggedIn

            		pop.Connect("mail.domain.com")

            		' Set "download the complete mailbox statistics" mode to increase the delay 
            		' between LoggedIn event and Login method completion.
            		pop.InboxPreloadOptions = Pop3InboxPreloadOptions.List Or Pop3InboxPreloadOptions.Uidl

            		' Authenticate the user, log in the mailbox, and download the mailbox statistics.
            		pop.Login("jdoe", "secret")

            		Console.WriteLine("The user account statistics received at " & _
            			DateDiff(DateInterval.Hour, DateTime.Now, DateTime.Today))

            		pop.Disconnect()
            	End Sub
            End Class

