            		Console.WriteLine(e.Data.Length & " bytes received")
            	End Sub

            	Private Shared Sub OnMessageDataChunkReceived(ByVal sender As Object, _
            		ByVal e As Pop3MessageDataChunkReceivedEventArgs)
            		Console.WriteLine(e.BytesJustReceived & " bytes of the message #" & _
            			e.MessageNumber & " received")
            	End Sub

            	' The actual code.
            	Shared Sub Main(ByVal args As String())
            		Dim pop As New Pop3

            		' Subscribe to events.
            		AddHandler pop.DataReceived, AddressOf OnDataReceived
            		AddHandler pop.MessageDataChunkReceived, _
            			AddressOf OnMessageDataChunkReceived

            		pop.Connect("mail.domain.com")
            		pop.Login("jdoe", "secret")

            		' Completely download the last message in the inbox.
            		Dim msg As MailMessage
            		msg = pop.DownloadEntireMessage(pop.InboxMessageCount)

            		pop.Disconnect()
            	End Sub
            End Class

