            		If (TypeOf e.Reason Is IMailBeeLoginException) Then
            			Console.WriteLine(e.Reason.Message) ' Login error.
            		End If
            	End Sub

            	' The actual code.
            	Shared Sub Main()
            		Dim mailer As New Smtp

            		' Specify SMTP server and authentication settings.
            		Dim server As New SmtpServer("smtp.somedomain.com", "jdoe", "badpassword")
            		server.IgnoreLoginFailure = True
            		mailer.SmtpServers.Add(server)

            		' Subscribe to the ErrorOccurred event.
            		AddHandler mailer.ErrorOccurred, AddressOf OnErrorOccurred

            		mailer.Connect()
            		mailer.Hello()

            		' Authenticate the user "jdoe".
            		mailer.Login()

            		mailer.Disconnect()
            	End Sub
            End Class

This WinForms sample demonstrates using asynchronous authentication in conjunction
            with a callback function.

            // To use the code below, import MailBee namespaces at the top of your code.
            using MailBee;
            using MailBee.SmtpMail;

            // Put the code below inside your class.

            // Login callback function.
            private void LoginCallback(IAsyncResult result)
            {
            	Smtp mailer = (Smtp)result.AsyncState;

            	mailer.EndLogin();
            	MessageBox.Show("Authentication succeeded");

            	// Close the connection.
            	mailer.Disconnect();
            }

            // The actual code.
            private void Form1_Load(object sender, System.EventArgs e)
            {
            	Smtp mailer = new Smtp();

            	mailer.SmtpServers.Add("smtp.somehost.com", "jdoe", "secret");

            	// Connect to SMTP server and say Hello.
            	mailer.Connect();
            	mailer.Hello();

            	// Initiate an asynchronous authentication attempt.
            	mailer.BeginLogin(new AsyncCallback(LoginCallback), mailer);
            }


            ' To use the code below, import MailBee namespaces at the top of your code.
            Imports MailBee
            Imports MailBee.SmtpMail

            ' Put the code below inside your class.

            ' Login callback function.
            Private Sub LoginCallback(ByVal result As IAsyncResult)
            	Dim mailer As Smtp = result.AsyncState

            	mailer.EndLogin()
            	MsgBox("Authentication succeeded")

            	' Close the connection.
            	mailer.Disconnect()
            End Sub

            ' The actual code.
            Private Sub Form1_Load(ByVal sender As Object, ByVal e As System.EventArgs)
            	Dim mailer As New Smtp

            	mailer.SmtpServers.Add("smtp.somehost.com", "jdoe", "secret")

            	' Connect to SMTP server and say Hello.
            	mailer.Connect()
            	mailer.Hello()

            	' Initiate an asynchronous authentication attempt.
            	mailer.BeginLogin(New AsyncCallback(AddressOf LoginCallback), mailer)
            End Sub

This sample connects to the SMTP server, issues HELP command, and displays
            the server response to this command. If HELP command is not supported by the server,
            MailBee will throw . It's caught in
            the sample code, and the proper error message is displayed.

            // To use the code below, import these namespaces at the top of your code.
            using System;
            using MailBee;
            using MailBee.SmtpMail;

            // The actual code (put it into a method of your class)

            Smtp mailer = new Smtp();
            mailer.SmtpServers.Add("smtp.host.com");
            mailer.Connect();
            try
            {
            	// Send HELP command.
            	mailer.ExecuteCustomCommand("HELP\r\n");
            	Console.WriteLine(mailer.GetServerResponse());
            }
            catch (MailBeeSmtpNegativeResponseException e)
            {
            	// The given SMTP server does not support HELP command.
            	Console.WriteLine(e.Message);
            	Console.WriteLine("Server response is: " + mailer.GetServerResponse());
            }
            mailer.Disconnect();


            ' To use the code below, import MailBee namespaces at the top of your code.
            Imports MailBee
            Imports MailBee.SmtpMail

            ' The actual code (put it into a method of your class)

            Dim mailer As New Smtp
            mailer.SmtpServers.Add("smtp.host.com")
            mailer.Connect()
            Try
            	' Send HELP command.
            	mailer.ExecuteCustomCommand("HELP\r\n")
            	Console.WriteLine(mailer.GetServerResponse())
            Catch e As MailBeeSmtpNegativeResponseException
            	' The given SMTP server does not support HELP command.
            	Console.WriteLine(e.Message)
            	Console.WriteLine("Server response is: " & mailer.GetServerResponse())
            End Try
            mailer.Disconnect()

This WinForms samples verifies e-mail address syntax by sending VRFY command to the SMTP server
            (the SMTP server must support VRFY command). The sample gets notified of VRFY command completion
            by using a callback function.

            // To use the code below, import MailBee namespaces at the top of your code.
            using MailBee;
            using MailBee.SmtpMail;

            // Put the code below inside your class.

            // ExecuteCustomCommand callback function.
            private void ExecuteCustomCommandCallback(IAsyncResult result)
            {
            	Smtp mailer = (Smtp)result.AsyncState;

            	try
            	{
            		// If this method does not throw exception, VRFY command 
            		// is supported, and the e-mail address validation passed.
            		// It's up to the server to decide whether to validate just 
            		// syntax or check if such e-mail address really exists.
            		mailer.EndExecuteCustomCommand();
            		MessageBox.Show("The e-mail address is correct");
            	}
            	catch (MailBeeSmtpNegativeResponseException e)
            	{
            		MessageBox.Show(e.ResponseString);
            	}

            	// Close the connection.
            	mailer.Disconnect();
            }

            // The actual code.
            private void Form1_Load(object sender, System.EventArgs e)
            {
            	Smtp mailer = new Smtp();

            	mailer.SmtpServers.Add("mail.host.com");

            	// Connect to SMTP server and say Hello.
            	mailer.Connect();
            	mailer.Hello();

            	// Initiate an asynchronous e-mail address verification attempt.
            	mailer.BeginExecuteCustomCommand("VRFY user@domain.com\r\n",
            		new AsyncCallback(ExecuteCustomCommandCallback), mailer);
            }


            ' To use the code below, import MailBee namespaces at the top of your code.
            Imports MailBee
            Imports MailBee.SmtpMail

            ' Put the code below inside your class.

            ' ExecuteCustomCommand callback function.
            Private Sub ExecuteCustomCommandCallback(ByVal result As IAsyncResult)
            	Dim mailer As Smtp = result.AsyncState

            	Try
            		' If this method does not throw exception, VRFY command 
            		' is supported, and the e-mail address validation passed.
            		' It's up to the server to decide whether to validate just 
            		' syntax or check if such e-mail address really exists.
            		mailer.EndExecuteCustomCommand()
            		MsgBox("The e-mail address is correct")
            	Catch e As MailBeeSmtpNegativeResponseException
            		MsgBox.Show(e.ResponseString)
            	End Try

            	' Close the connection.
            	mailer.Disconnect()
            End Sub

            ' The actual code.
            Private Sub Form1_Load(ByVal sender As Object, ByVal e As System.EventArgs)
            	Dim mailer As New Smtp

            	mailer.SmtpServers.Add("mail.host.com")

            	' Connect to SMTP server and say Hello.
            	mailer.Connect()
            	mailer.Hello()

            	' Initiate an asynchronous e-mail address verification attempt.
            	mailer.BeginExecuteCustomCommand("VRFY user@domain.com\r\n", _
            		New AsyncCallback(AddressOf ExecuteCustomCommandCallback), mailer)
            End Sub

This sample relays the mail message from .EML file to the recipients, using
            the following configuration (which targets high reliability of send mail operation):


Attempt to send to SMTP relay server at smtp1.domain.com first

If this fails, try to send to backup SMTP relay server at smtp2.domain.com

If this fails too, try direct send mode. To discover SMTP servers
            accepting mails for recipients domains (i.e. to perform DNS MX lookup),
            use DNS servers specified by the operating system

If the DNS servers reported by OS are not available or down,
            use backup DNS server at 10.20.30.40


            // To use the code below, import MailBee namespaces at the top of your code.
            using MailBee;
            using MailBee.SmtpMail;
            using MailBee.Mime;

            // The actual code (put it into a method of your class)

            Smtp mailer = new Smtp();

            mailer.SmtpServers.Add("smtp1.domain.com", 25, 0);	// top priority.
            mailer.SmtpServers.Add("smtp2.domain.com", 25, 1);	// low priority.

            // Auto-detect DNS servers from OS settings or config file.
            mailer.DnsServers.Autodetect();

            // Add backup DNS server of low priority (priority of DNS servers 
            // registered in OS is usually in the range 0 to 10).
            mailer.DnsServers.Add("10.20.30.40", 100);

            // Send the message from the file.
            mailer.RelayFromEmlFile(@"C:\Temp\message.eml", "from@domain.com",
            	new EmailAddressCollection("to@company.com"));


            ' To use the code below, import MailBee namespaces at the top of your code.
            Imports MailBee
            Imports MailBee.SmtpMail
            Imports MailBee.Mime

            ' The actual code (put it into a method of your class)

            Dim mailer As New Smtp

            mailer.SmtpServers.Add("smtp1.domain.com", 25, 0)	' top priority.
            mailer.SmtpServers.Add("smtp2.domain.com", 25, 1)	' low priority.

            ' Auto-detect DNS servers from OS settings or config file.
            mailer.DnsServers.Autodetect()

            ' Add backup DNS server of low priority (priority of DNS servers 
            ' registered in OS is usually in the range 0 to 10).
            mailer.DnsServers.Add("10.20.30.40", 100)

            ' Send the message from the file.
            mailer.RelayFromEmlFile("C:\Temp\message.eml", "from@domain.com", _
            	New EmailAddressCollection("to@company.com"))

This sample relays the mail message from .EML file to 3 recipients by submitting
            the message to the SMTP relay server.
             event is used to report which recipients have
            been accepted or refused. 

            using System;
            using MailBee;
            using MailBee.SmtpMail;
            using MailBee.Mime;

            class Sample
            {
            	// MessageRecipientSubmitted event handler.
            	private static void OnMessageRecipientSubmitted(object sender,
            		SmtpMessageRecipientSubmittedEventArgs e)
            	{
            		if (e.Result)
            		{
            			Console.WriteLine(e.RecipientEmail + " accepted by the server");
            		}
            		else
            		{
            			Console.WriteLine(e.RecipientEmail + " rejected by the server");
            		}
            	}

            	// The actual code.
            	static void Main(string[] args)
            	{
            		Smtp mailer = new Smtp();

            		// Specify SMTP server to use, and enable SMTP authentication.
            		SmtpServer server = new SmtpServer("smtp-relay.host.com", "jdoe", "secret");
            		mailer.SmtpServers.Add(server);

            		// Subscribe to the MessageRecipientSubmitted event.
            		mailer.MessageRecipientSubmitted +=
            			new SmtpMessageRecipientSubmittedEventHandler(OnMessageRecipientSubmitted);

            		// Send the message from the file to 3 recipients.
            		mailer.RelayFromEmlFile(@"C:\Temp\message.eml", "jdoe@host.com",
            			"client@company.com, b.smith@domain1.com, j.smith@domain2.com");
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.SmtpMail
            Imports MailBee.Mime

            Class Sample
            	' MessageRecipientSubmitted event handler.
            	Private Shared Sub OnMessageRecipientSubmitted(ByVal sender As Object, _
            		ByVal e As SmtpMessageRecipientSubmittedEventArgs)
            		If (e.Result) Then
            			Console.WriteLine(e.RecipientEmail + " accepted by the server")
            		Else
            			Console.WriteLine(e.RecipientEmail + " rejected by the server")
            		End If
            	End Sub

            	' The actual code.
            	Shared Sub Main(ByVal args As String())
            		Dim mailer As New Smtp

            		' Specify SMTP server to use, and enable SMTP authentication.
            		Dim server As New SmtpServer("smtp-relay.host.com", "jdoe", "secret")
            		mailer.SmtpServers.Add(server)

            		' Subscribe to the MessageRecipientSubmitted event.
            		AddHandler mailer.MessageRecipientSubmitted, AddressOf OnMessageRecipientSubmitted

            		' Send the message from the file to 3 recipients.
            		mailer.RelayFromEmlFile("C:\Temp\message.eml", "jdoe@host.com", _
            			"client@company.com, b.smith@domain1.com, j.smith@domain2.com")
            	End Sub
            End Class

This console sample demonstrates asynchronous relaying the mail message using direct send
            approach (via DNS MX lookup). No callback function is used.

            using System;
            using MailBee;
            using MailBee.SmtpMail;
            using MailBee.Mime;

            class Sample
            {
            	// The actual code.
            	static void Main(string[] args)
            	{
            		Smtp mailer = new Smtp();

            		// Get the list of DNS servers from OS settings or the config file.
            		mailer.DnsServers.Autodetect();

            		// Initiate an asynchronous relay.
            		mailer.BeginRelayFromEmlFile(@"C:\Temp\message.eml", "kathy@company.com",
            			new EmailAddressCollection("mike@domain1.com, bob@domain2.com"), null, null);

            		// Simulate some lengthy work here. At the same time, 
            		// the message is being sent to recipients domains on another thread.
            		System.Threading.Thread.Sleep(3000);

            		// End the relay request. If send mail operation is still in progress, 
            		// the method will wait until it's finished.
            		mailer.EndRelayFromEmlFile();

            		// Print the outcome.
            		Console.WriteLine("Successfully sent to: " + mailer.GetAcceptedRecipients().ToString());
            		Console.WriteLine("Not sent to: " + mailer.GetRefusedRecipients().ToString());
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.SmtpMail
            Imports MailBee.Mime

            Class Sample
            	' The actual code.
            	Shared Sub Main(ByVal args As String())
            		Dim mailer As New Smtp

            		' Get the list of DNS servers from OS settings or the config file.
            		mailer.DnsServers.Autodetect()

            		' Initiate an asynchronous relay.
            		mailer.BeginRelayFromEmlFile("C:\Temp\message.eml", "kathy@company.com", _
            			New EmailAddressCollection("mike@domain1.com, bob@domain2.com"), Nothing, Nothing)

            		' Simulate some lengthy work here. At the same time, 
            		' the message is being sent to recipients domains on another thread.
            		System.Threading.Thread.Sleep(3000)

            		' End the relay request. If send mail operation is still in progress, 
            		' the method will wait until it's finished.
            		mailer.EndRelayFromEmlFile()

            		' Print the outcome.
            		Console.WriteLine("Successfully sent to: " & mailer.GetAcceptedRecipients().ToString())
            		Console.WriteLine("Not sent to: " & mailer.GetRefusedRecipients().ToString())
            	End Sub
            End Class

