	End Sub

	Shared Sub Main(ByVal args() As String)
		Dim mailer As Smtp = New Smtp

		' Logging into a file is useful for troubleshooting.
		mailer.Log.Filename = "C:\log.txt"
		mailer.Log.Enabled = True
		mailer.Log.Format = LogFormatOptions.AddContextInfo
		mailer.Log.Clear()

		' Subscribe to 3 events to track send bulk mail progress.
		AddHandler mailer.MergingMessage, AddressOf mailer_MergingMessage
		AddHandler mailer.SubmittingMessageToPickupFolder, AddressOf mailer_SubmittingMessageToPickupFolder
		AddHandler mailer.MessageSubmittedToPickupFolder, AddressOf mailer_MessageSubmittedToPickupFolder

		' Setup e-mail message header template for mail merge.
		mailer.Message.From.AsString = "John Doe <john.doe@domain.com>"
		mailer.Message.To.AsString = "##Name## <##Email##>"
		mailer.Message.Subject = "Our Jan/2007 newsletter"

		' Setup HTML body template.
		mailer.Message.BodyHtmlText = "<html>##Body##</html>"

		' Specify database connection string (it may be different in your case).
		Dim connParams As String = "Provider=Microsoft.Jet.OLEDB.4.0; Data Source=C:\Newsletter.mdb;"

		' Connect to the database and populate mail merge job to-do list with
		' the data from "mailing_list" table.
		Dim conn As OleDbConnection
		Try
			conn = New OleDbConnection(connParams)

			' Open the connection and get the data.
			Dim command As OleDbCommand = New OleDbCommand("SELECT * FROM mailing_list", conn)
			conn.Open()
			Dim adapter As OleDbDataAdapter = New OleDbDataAdapter
			Dim table As DataTable = New DataTable
			adapter.SelectCommand = command
			adapter.Fill(table)

			mailer.AddJob(Nothing, Nothing, Nothing, table)
		Finally
			If Not conn Is Nothing Then
				conn.Close()
			End If

		End Try

		' Run mail merge.
		mailer.SubmitJobsToPickupFolder("C:\Inetpub\mailroot\Pickup")
	End Sub
End Class

This sample sends a mail message in direct send mode (no SMTP relay server
            is used). To make sure all recipients will receive the message, 
            is called first. Extensive error checking is performed in 
            event handler to learn why the error occurred.

            using System;
            using MailBee;
            using MailBee.SmtpMail;
            using MailBee.DnsMX;
            using MailBee.Mime;

            class Sample
            {
            	// ErrorOccurred event handler.
            	private static void OnErrorOccurred(object sender, ErrorEventArgs e)
            	{
            		if (e.Reason is MailBeeSmtpRefusedRecipientException)
            		{
            			MailBeeSmtpRefusedRecipientException ex =
            				e.Reason as MailBeeSmtpRefusedRecipientException;
            			Console.WriteLine(ex.RefusedRecipientEmail + " was refused by the server");
            		}
            		else if (e.Reason is MailBeeDnsNameErrorException)
            		{
            			MailBeeDnsNameErrorException ex = e.Reason as MailBeeDnsNameErrorException;
            			Console.WriteLine(ex.HostName + " is unknown host");
            		}
            		else if (e.Reason is MailBeeDnsProtocolNegativeResponseException)
            		{
            			MailBeeDnsProtocolNegativeResponseException ex =
            				e.Reason as MailBeeDnsProtocolNegativeResponseException;
            			Console.WriteLine("DNS server returned error for " + ex.HostName);
            		}
            		else if (e.Reason is MailBeeConnectionException)
            		{
            			MailBeeConnectionException ex = e.Reason as MailBeeConnectionException;
            			if (ex.Protocol == TopLevelProtocolType.Smtp)
            			{
            				Console.WriteLine("Connection problem with " +
            					ex.RemoteHostName + " SMTP MX server ");
            			}
            		}
            		else
            		{
            			Console.WriteLine(e.Reason.Message);
            		}
            	}

            	// The actual code.
            	static void Main(string[] args)
            	{
            		Smtp mailer = new Smtp();

            		// Get DNS servers list from OS settings or config file 
            		// (like app.config).
            		mailer.DnsServers.Autodetect();

            		mailer.From.AsString = "John Doe <jdoe@domain.com>";

            		// Specify recipients.
            		mailer.To.AsString = "no-display-name@website.com, \"Bill Smith, Jr.\" &lt;bill.smith@company.com&gt;";
            		mailer.To.AddFromString("Kathy Smith &lt;k.smith@domain.com&gt;");
            		mailer.To.AddFromString("email@address.com");
            		mailer.To.Add("Mike Jackson, Sales Manager", "mj@company.com", "Sales Department");

            		mailer.Subject = "Newsletter";
            		mailer.BodyPlainText = "This is our weekly newsletter.";

            		// Subscribe to the ErrorOccurred event.
            		mailer.ErrorOccurred += new ErrorEventHandler(OnErrorOccurred);

            		// Make sure all the recipients are ok.
            		if (mailer.TestSend(SendFailureThreshold.AllRecipientsFailed) != TestSendResult.OK)
            		{
            			Console.WriteLine("No recipients can receive the message.");
            		}
            		else if (mailer.GetRefusedRecipients().Count > 0)
            		{
            			Console.WriteLine("The following recipients failed: " +
            				mailer.GetRefusedRecipients().ToString());
            		}
            		else
            		{
            			Console.WriteLine("All recipients are ok. Will send the message now.");
            			mailer.Send();
            			Console.WriteLine("Sent to: " + mailer.GetAcceptedRecipients().ToString());
            		}
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.SmtpMail
            Imports MailBee.DnsMX
            Imports MailBee.Mime

            Class Sample
            	' ErrorOccurred event handler.
            	Private Shared Sub OnErrorOccurred(ByVal sender As Object, ByVal e As ErrorEventArgs)
            		If (TypeOf e.Reason Is MailBeeSmtpRefusedRecipientException) Then
            			Dim ex As MailBeeSmtpRefusedRecipientException = e.Reason
            			Console.WriteLine(ex.RefusedRecipientEmail & " was refused by the server")
            		ElseIf (TypeOf e.Reason Is MailBeeDnsNameErrorException) Then
            			Dim ex As MailBeeDnsNameErrorException = e.Reason
            			Console.WriteLine(ex.HostName & " is unknown host")
            		ElseIf (TypeOf e.Reason Is MailBeeDnsProtocolNegativeResponseException) Then
            			Dim ex As MailBeeDnsProtocolNegativeResponseException = e.Reason
            			Console.WriteLine("DNS server returned error for " + ex.HostName)
            		ElseIf (TypeOf e.Reason Is MailBeeConnectionException) Then
            			Dim ex As MailBeeConnectionException = e.Reason
            			If (ex.Protocol = TopLevelProtocolType.Smtp) Then
            				Console.WriteLine("Connection problem with " & _
            					ex.RemoteHostName & " SMTP MX server ")
            			End If
            		Else
            			Console.WriteLine(e.Reason.Message)
            		End If
            	End Sub

            	' The actual code.
            	Shared Sub Main(ByVal args As String())
            		Dim mailer As New Smtp

            		' Get DNS servers list from OS settings or config file 
            		' (like app.config).
            		mailer.DnsServers.Autodetect()

            		mailer.From.AsString = "John Doe <jdoe@domain.com>"

            		' Specify recipients.
            		mailer.To.AsString = "no-display-name@website.com, ""Bill Smith, Jr."" <bill.smith@company.com>"
            		mailer.To.AddFromString("Kathy Smith <k.smith@domain.com>")
            		mailer.To.AddFromString("email@address.com")
            		mailer.To.Add("Mike Jackson, Sales Manager", "mj@company.com", "Sales Department")

            		mailer.Subject = "Newsletter"
            		mailer.BodyPlainText = "This is our weekly newsletter."

            		' Subscribe to the ErrorOccurred event.
            		AddHandler mailer.ErrorOccurred, AddressOf OnErrorOccurred

            		' Make sure all the recipients are ok.
            		If (mailer.TestSend(SendFailureThreshold.AllRecipientsFailed) <> TestSendResult.OK) Then
            			Console.WriteLine("No recipients can receive the message.")
            		ElseIf (mailer.GetRefusedRecipients().Count > 0) Then
            			Console.WriteLine("The following recipients failed: " & _
            				mailer.GetRefusedRecipients().ToString())
            		Else
            			Console.WriteLine("All recipients are ok. Will send the message now.")
            			mailer.Send()
            			Console.WriteLine("Sent to: " & mailer.GetAcceptedRecipients().ToString())
            		End If
            	End Sub
            End Class

Adding an attachment to the message using Smtp.AddAttachment and Smtp.Message.Attachments.Add methods.

            // To use the code below, import MailBee namespaces at the top of your code.
            using MailBee;
            using MailBee.SmtpMail;

            // The actual code (put it into a method of your class)

            Smtp mailer = new Smtp();

            // Add attachment under real name.
            mailer.AddAttachment(@"C:\My Documents\Report.xls");

            // Add B4DVIS9H.TMP file as "Annual report.doc".
            mailer.Message.Attachments.Add(@"C:\DataFiles\B4DVIS9H.TMP", "Annual report.doc");


            ' To use the code below, import MailBee namespaces at the top of your code.
            Imports MailBee
            Imports MailBee.SmtpMail

            ' The actual code (put it into a method of your class)

            Dim mailer As New Smtp

            ' Add attachment under real name.
