            		// Subscribe to the DataSent event.
            		mailer.DataSent += new DataTransferEventHandler(OnDataSent);

            		// Produce some POP3 and SMTP traffic by connecting in POP-before-SMTP mode.
            		mailer.Connect();
            		mailer.Hello();
            		mailer.Disconnect();
            	}
            }


