            		Console.WriteLine(e.Data.Length & " bytes sent")
            	End Sub

            	Private Shared Sub OnMessageDataChunkSent(ByVal sender As Object, _
            		ByVal e As SmtpMessageDataChunkSentEventArgs)
            		Console.WriteLine(e.BytesJustSent & " bytes of the message " & _
            			e.MailMessage.MessageID & " sent")
