This sample displays the current user name, then impersontates the current thread and displays
            the current user name again (it's now different), then reverts back to the original user account and displays its name.

            using System;
            using System.Security.Principal;

            using MailBee;
            using MailBee.Security;

            class Sample
            {
            	static void Main(string[] args)
            	{
            		// Display the current domain\user name (such as "OurCompany\JohnDoe").
            		Console.WriteLine(WindowsIdentity.GetCurrent().Name);

            		// Impersonate as Administrator user of the current computer.
            		Impersonation imperson = new Impersonation();
            		imperson.LogonAs("Administrator", null, "secret");

            		// Display the current domain\user name (such as "JohnComputer\Administrator").
            		Console.WriteLine(WindowsIdentity.GetCurrent().Name);

            		// Revert back to the original user account.
            		imperson.Logoff();

            		// Display the current domain\user name (such as "OurCompany\JohnDoe").
            		Console.WriteLine(WindowsIdentity.GetCurrent().Name);
            	}
            }


            Imports System
            Imports System.Security.Principal

            Imports MailBee
            Imports MailBee.Security

            Class Sample
            	Shared Sub Main(ByVal args() As String)
            		' Display the current domain\user name (such as "OurCompany\JohnDoe").
            		Console.WriteLine(WindowsIdentity.GetCurrent().Name)

            		' Impersonate as Administrator user of the current computer.
            		Dim imperson As Impersonation = New Impersonation
            		imperson.LogonAs("Administrator", Nothing, "secret")

            		' Display the current domain\user name (such as "JohnComputer\Administrator").
            		Console.WriteLine(WindowsIdentity.GetCurrent().Name)

            		' Revert back to the original user account.
            		imperson.Logoff()

            		' Display the current domain\user name (such as "OurCompany\JohnDoe").
            		Console.WriteLine(WindowsIdentity.GetCurrent().Name)
            	End Sub
            End Class

