            End Module

This sample demonstrates loading 1000 bytes of the message data from a file using a buffer of a fixed length (100 bytes). It's assumed the file
            is at least 1000 bytes in size.
            In real world applications, it's easier to load a message from a file/stream/memory using  method and its overloads.

            using System;
            using System.IO;
            using MailBee;
            using MailBee.Mime;

            class Sample
            {
            	static void Main(string[] args)
            	{
            		MailMessage msg = new MailMessage();
            		string filename = @"C:\Docs\TestMail.eml";

            		// Open the file for reading.
            		using (FileStream fs = new FileStream(filename, FileMode.Open))
            		{
            			// Create a 100 bytes buffer.
            			byte[] bytes = new byte[100];

            			for (int i = 0; i < 10; i++)
            			{
            				// Read a portion of data (100 bytes) from the file and append it to the message source.
            				fs.Read(bytes, i * bytes.Length, bytes.Length);
            				msg.AppendChunk(bytes);
            			}
            		}

            		// The message will be parsed at this point (when we access any of its properties).
            		Console.WriteLine(msg.Subject);
            	}
            }


            Imports System
            Imports System.IO
            Imports MailBee
            Imports MailBee.Mime

            Module Sample
            	Sub Main(ByVal args As String())
            		Dim msg = New MailMessage
            		Dim filename As String = "C:\Docs\TestMail1.eml"

            		Dim fs As FileStream
            		Try
            			' Open the file for reading.
            			fs = New FileStream(filename, FileMode.Open)

            			' Create a 100 bytes buffer.
            			Dim bytes As Byte()
            			ReDim bytes(100)

            			For i As Integer = 1 To 10
            				' Read a portion of data (100 bytes) from the file and append it to the message source.
            				fs.Read(bytes, i * bytes.Length, bytes.Length)
            				msg.AppendChunk(bytes)
            			Next
            		Finally
            			If Not fs Is Nothing Then
            				fs.Close()
            			End If
            		End Try

            		' The message will be parsed at this point (when we access any of its properties).
            		Console.WriteLine(msg.Subject)
            	End Sub
            End Module

This sample assemblies the message from 4 partial messages. It loads the first portion
            and then calls  for the remaining 3 portions.

            using MailBee;
            using MailBee.Mime;

            class Sample
            {
            	static void Main(string[] args)
            	{
            		MailMessage newMsg = new MailMessage();
            		MailMessage partialMsg = new MailMessage();
            		newMsg.LoadMessage(@"C:\Docs\splitmail\1of4.eml");

            		// Append 3 partial messages that are loaded from disk.
            		for (int i = 2; i <= 4; i++)
            		{
            			partialMsg.LoadMessage(string.Format(@"C:\Docs\splitmail\{0}of4.eml", i));
            			newMsg.AppendPartialMessage(partialMsg);
            		}

            		// Save the entire message to disk.
            		newMsg.SaveMessage(@"C:\Temp\entire.eml");
            	}
            }


            Imports MailBee
            Imports MailBee.Mime

            Module Sample
            	Sub Main(ByVal args As String())
            		Dim newMsg = New MailMessage
            		Dim partialMsg = New MailMessage
            		newMsg.LoadMessage("C:\Docs\splitmail\1of4.eml")

            		' Append 3 partial messages that are loaded from disk.
            		For i As Integer = 2 To 4
            			partialMsg.LoadMessage(String.Format("C:\Docs\splitmail\{0}of4.eml", i))
            			newMsg.AppendPartialMessage(partialMsg)
            		Next

            		' Save the entire message to disk.
            		newMsg.SaveMessage("C:\Temp\entire.eml")
            	End Sub
            End Module

This sample downloads the message from the specified POP3 account, removes all message attachments and recipients
            (other data is not removed), and saves this message to disk.

            using MailBee;
            using MailBee.Pop3Mail;
            using MailBee.Mime;

            class Sample
            {
            	static void Main(string[] args)
            	{
