            		' Set the e-mail address of confirmation message recipient.
            		msg.ConfirmReceipt = "jdoe@domain.com"

            		' Save message to the specified .eml file.
            		msg.SaveMessage("C:\Temp\MyMail.eml")
            	End Sub
            End Module

