This sample gets the headers of the message and the first 10 lines of its body message from the POP3 server and determines whatever the message is entire, partial or
            incomplete. It will be partial if it's a composite message split into smaller ones, or it will be entire if the entire message body fits into 10 lines,
            or it will be incomplete if its body does not fit into 10 lines which have been downloaded.

            using System;
            using MailBee;
            using MailBee.Mime;
            using MailBee.Pop3Mail;

            class Sample
            {
            	static void Main(string[] args)
            	{
            		// Download the first mail message from the specified POP3 account.
            		MailMessage msg = Pop3.QuickDownloadMessage("mail.domain.com", "jdoe", "password", 1, 10);

            		// Check whatever the message is entire, partial,
            		// or it was received incompletely.
            		if (msg.IsEntire)
            		{
            			Console.WriteLine("The message was completely received");
            		}
            		else if (msg.PartCount > 1)
            		{
            			Console.WriteLine(@"The message is partial (part index is " + 
            				msg.PartIndex + ")");
            		}
            		else
            		{
            			Console.WriteLine(@"The message was not completely received 
            				(the body is larger than 10 lines downloaded)");
            		}
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.Mime
            Imports MailBee.Pop3Mail

            Module Sample
            	Sub Main(ByVal args As String())
            		' Download the first mail message from the specified POP3 account.
            		Dim msg As MailMessage = Pop3.QuickDownloadMessage("mail.domain.com", "jdoe", "password", 1, 10)

            		' Check whatever the message is entire, partial,
            		' or it was received incompletely.
            		If msg.IsEntire Then
            			Console.WriteLine("The message was completely received")
            		ElseIf msg.PartCount > 1 Then
            			Console.WriteLine("The message is partial (part index is " & _
            				msg.PartIndex.ToString & ")")
            		Else
            			Console.WriteLine("The message was not completely received " & _
            				"(the body is larger than 10 lines downloaded)")
            		End If
            	End Sub
            End Module

