This WinForms sample demonstrates asynchronous unselecting the Inbox folder and expunging of all deleted messages if any.

            // To use the code below, import MailBee namespaces at the top of your code.
            using MailBee;
            using MailBee.ImapMail;

            // Put the code below inside your class.

            Imap imp = null;
            bool finished = false;

            // A callback function. Since it's called on Imap worker thread,
            // it cannot update controls on the form or otherwise access UI.
            // The only UI-related action permitted in worker thread is
            // displaying a message box (MessageBox.Show).
            // Any updates of UI must take place on message loop thread.
            // See BeginSearch method example on how to update UI in callbacks.
            private void CloseCallback(IAsyncResult result)
            {
            	imp.EndClose();
            	finished = true;
            }

            // The actual code.
            private void Form1_Load(object sender, System.EventArgs e)
            {
            	imp = new Imap();

            	// Connect to the server, login and select inbox.
            	imp.Connect("mail.company.com");
            	imp.Login("jdoe@company.com", "secret");
            	imp.SelectFolder("Inbox");

            	// Start async close/expunge operation.
            	imp.BeginClose(true, new AsyncCallback(CloseCallback), null);

            	// Wait until it's finished.
            	while (!finished)
            	{
            		// Process events on message loop thread.
            		// Alternatively, we could set imp.RaiseEventsOnMessageLoop
            		// to false and use imp.Wait method to perform waiting.
            		System.Threading.Thread.Sleep(1);
            		Application.DoEvents();
            	}

            	// Close the connection.
            	imp.Disconnect();
            }


            ' To use the code below, import MailBee namespaces at the top of your code.
            Imports MailBee
            Imports MailBee.ImapMail

            ' Put the code below inside your class.
            Dim imp As Imap = Nothing
            Dim finished As Boolean = False

            ' A callback function. Since it's called on Imap worker thread,
            ' it cannot update controls on the form or otherwise access UI.
            ' The only UI-related action permitted in worker thread is
            ' displaying a message box (MessageBox.Show).
            ' Any updates of UI must take place on message loop thread.
            ' See BeginSearch method example on how to update UI in callbacks.
            Private Sub CloseCallback(ByVal result As IAsyncResult)
            	imp.EndClose()
            	finished = True
            End Sub

            ' The actual code.
            Private Sub Form1_Load(ByVal sender As Object, ByVal e As System.EventArgs)
            	Dim imp As New Imap

            	' Connect to the server, login and select inbox.
            	imp.Connect("imap.domain.com")
            	imp.Login("jdoe", "secret")
            	imp.SelectFolder("Inbox")

            	' Start async close/expunge operation.
            	imp.BeginClose(True, New AsyncCallback(AddressOf CloseCallback), Nothing)

            	' Wait until it's finished.
            	While Not finished
            		' Process events on message loop thread.
            		' Alternatively, we could set imp.RaiseEventsOnMessageLoop
            		' to false and use imp.Wait method to perform waiting.
            		System.Threading.Thread.Sleep(1)
            		Application.DoEvents()
            	End While

            	' Close the connection.
            	imp.Disconnect()
            End Sub

This sample marks the first message in inbox as deleted, and then expunges it. It there are some other messages marked as deleted
            in inbox, they will not be expunged.

            using System;
            using MailBee;
            using MailBee.ImapMail;

            class Sample
            {
            	static void Main(string[] args)
            	{
            		Imap imp = new Imap();

            		// Connect to the server and log in the account.
            		imp.Connect("imap.domain.com");
            		imp.Login("jdoe@domain.com", "secret");

            		// Select Inbox folder.
            		imp.SelectFolder("INBOX");

            		// Mark message #1 as deleted.
            		imp.SetMessageFlags("1", false,
            			SystemMessageFlags.Deleted, MessageFlagAction.Add);

            		// To expunge specific message, we need to know its UID.
            		EnvelopeCollection uidList = imp.DownloadEnvelopes("1", false, EnvelopeParts.Uid, 0);

            		if (uidList.Count > 0)
            		{
            			// Expunge message #1 by its UID.
            			imp.Expunge(uidList[0].Uid.ToString(), false);
            		}

            		// We do not call Close() here to prevent remaining messages
            		// marked as deleted from being expunged.
            		imp.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.ImapMail

            Module Sample
            	Sub Main()
            		Dim imp As New Imap

            		' Connect to the server and log in the account.
            		imp.Connect("imap4.company.com")
            		imp.Login("jdoe@company.com", "secret")

            		' Select Inbox folder.
            		imp.SelectFolder("INBOX")

            		' Mark message #1 as deleted.
            		imp.SetMessageFlags("1", False, _
            			SystemMessageFlags.Deleted, MessageFlagAction.Add)

            		' To expunge specific message, we need to know its UID.
            		Dim uidList As EnvelopeCollection = _
            			imp.DownloadEnvelopes("1", False, EnvelopeParts.Uid, 0)

            		If uidList.Count > 0 Then
            			' Expunge message #1 by its UID.
            			imp.Expunge(uidList(0).Uid.ToString(), False)
            		End If

            		' We do not call Close() here to prevent remaining messages
            		' marked as deleted from being expunged.
            		imp.Disconnect()
            	End Sub
            End Module

This sample removes last 10 messages from Sent folder. If there were another messages marked as deleted in this folder,
            they will be removed as well.

            using System;
            using MailBee;
            using MailBee.ImapMail;

            class Sample
            {
            	static void Main(string[] args)
            	{
            		Imap imp = new Imap();

            		// Connect to the server and log in the account.
            		imp.Connect("imap.domain.com");
            		imp.Login("jdoe@domain.com", "secret");

            		// Select Sent folder.
            		imp.SelectFolder("Sent");

            		// Mark last 10 messages as deleted.
            		if (imp.MessageCount >= 10)
            		{
            			string range = (imp.MessageCount - 9).ToString() + ":" + imp.MessageCount.ToString();
            			imp.SetMessageFlags(range, false, SystemMessageFlags.Deleted, MessageFlagAction.Add);
            		}

            		// Expunge all messages marked as deleted.
            		imp.Expunge();

            		imp.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.ImapMail

            Module Sample
            	Sub Main(ByVal args As String())
            		Dim imp As New Imap

            		' Connect to the server and log in the account.
            		imp.Connect("imap4.company.com")
            		imp.Login("jdoe@company.com", "secret")

            		' Select Sent folder.
            		imp.SelectFolder("Sent")

            		' Mark last 10 messages as deleted.
            		If (imp.MessageCount >= 10) Then
            			Dim range As String = (imp.MessageCount - 9).ToString() & _
            				":" & imp.MessageCount.ToString()
            			imp.SetMessageFlags(range, False, SystemMessageFlags.Deleted, _
            				MessageFlagAction.Add)
            		End If

            		' Expunge all messages marked as deleted.
            		imp.Expunge()

            		imp.Disconnect()
            	End Sub
            End Module

