This sample highlights common practices of IMAP4 search. You can also find
additional examples in  and  topics.

using System;
using MailBee;
using MailBee.ImapMail;

class Sample
{
	static void Main(string[] args)
	{
		Imap imp = new Imap();

		imp.Connect("imap.company.com");
		imp.Login("jdoe@company.com", "secret");

		// Select Inbox folder.
		imp.SelectFolder("INBOX");

		UidCollection uids = null;
		MessageNumberCollection msgNums = null;
		string input = null;

		// Determine charset to use. This will let us perform search of
		// messages written in the charset of the current computer.
		string localComputerCharset = System.Text.Encoding.Default.WebName;

		// -----------------------------------------------------------------
		// Section 1: Search in Subject field.
		// Demonstrates text search and usage of charsets (for international
		// applications) and quoted strings (for searching arbitrary text,
		// including spaces, quotes, and other special characters).
		// -----------------------------------------------------------------

		Console.WriteLine("Input string to be searched in Subject field:");
		input = Console.ReadLine();

		// 1.1: US-ASCII search (case-insensitive).

		// 1.1.1: Search for UIDs.
		uids = (UidCollection)imp.Search(true,
			"SUBJECT " + ImapUtils.ToQuotedString(input), null);

		Console.WriteLine();
		Console.WriteLine("UIDs of found messages (US-ASCII mode):");
		Console.WriteLine(uids.ToString());

		// 1.1.2: Search for ordinal message numbers. This demonstrates
		// how to get search results as message numbers rather UIDs.
		msgNums = (MessageNumberCollection)imp.Search(false,
			"SUBJECT " + ImapUtils.ToQuotedString(input), null);

		Console.WriteLine();
		Console.WriteLine("Message numbers of found messages (US-ASCII mode):");
		Console.WriteLine(msgNums.ToString());

		// 1.2: Search with support of international characters of the current
		// computer's charset. US-ASCII characters will be searched in case-insensitive
		// manner but there is no such guarantee for non-US-ASCII characters.
		uids = (UidCollection)imp.Search(true,
			"SUBJECT " + ImapUtils.ToQuotedString(input),
			localComputerCharset);

		Console.WriteLine();
		Console.WriteLine("UIDs of found messages (" + localComputerCharset + " mode):");
		Console.WriteLine(uids.ToString());

		// 1.3: Search with support of UTF-8. This will let us perform search of messages
		// written in UTF-8 (provided that UTF-8 data in messages are not encoded in
		// Base64, quoted-printable, etc).
		uids = (UidCollection)imp.Search(true,
			"SUBJECT " + ImapUtils.ToQuotedString(input),
			System.Text.Encoding.UTF8.WebName);

		Console.WriteLine();
		Console.WriteLine("UIDs of found messages (UTF-8 mode):");
		Console.WriteLine(uids.ToString());

		// -----------------------------------------------------------------
		// Section 2: Search of messages with specific flags set.
		// Also demonstrates how to specify OR and AND conditions.
		// -----------------------------------------------------------------

		// 2.1: Search for messages having \Deleted flag set.
		uids = (UidCollection)imp.Search(true, "DELETED", null);

		Console.WriteLine();
		Console.WriteLine(@"UIDs of \Deleted messages:");
		Console.WriteLine(uids.ToString());

		// 2.2: Search for messages having \Deleted or \Seen flags set.
		uids = (UidCollection)imp.Search(true, "OR DELETED SEEN", null);

		Console.WriteLine();
		Console.WriteLine(@"UIDs of \Deleted or \Seen messages:");
		Console.WriteLine(uids.ToString());

		// 2.3: Search for messages having \Deleted or \Seen or \Answered flags set.
		uids = (UidCollection)imp.Search(true, "OR (OR DELETED SEEN) ANSWERED", null);

		Console.WriteLine();
		Console.WriteLine(@"UIDs of \Deleted or \Seen or \Answered messages:");
		Console.WriteLine(uids.ToString());

		// 2.4: Search for messages having \Deleted or (\Seen and \Answered) flags set.
		uids = (UidCollection)imp.Search(true, "OR DELETED (SEEN ANSWERED)", null);

		Console.WriteLine();
		Console.WriteLine(@"UIDs of \Deleted or (\Seen and \Answered) messages:");
		Console.WriteLine(uids.ToString());

		// ------------------------------------------------------------------
		// Section 3: Search of messages meeting complex criteria.
		// The criteria set (in C# syntax):
		// (size > 10000 || Flagged || !Spam) && BodyContains(string).
		// I.e. the message is larger than 10000 bytes or flagged or not spam
		// (there is no standard \Spam attribute so custom $Spam attribute
		// is used) and, besides that, the message must contain the specified
		// string (taken from user input) in the body.
		// ------------------------------------------------------------------

		uids = (UidCollection)imp.Search(true,
			"OR (OR LARGER 10000 FLAGGED) UNKEYWORD " + ImapUtils.ToQuotedString("$Spam") +
			" BODY " + ImapUtils.ToQuotedString(input), null);

		Console.WriteLine();
		Console.WriteLine("UIDs of messages meeting complex criteria:");
		Console.WriteLine(uids.ToString());

		// Disconnect from the server.
		imp.Disconnect();
	}
}


