            		Dim msgNums As MessageNumberCollection = CType(imp.EndSearch(), MessageNumberCollection)
            		MessageBox.Show(msgNums.ToString())
            	Catch e As MailBeeException
            		MessageBox.Show(e.Message)
            	End Try

            	' Update form's caption area to demonstrate
            	' that it's safe to update UI in this method.
            	Me.Text = "Search finished"
            End Sub

            ' A callback function. Since it's called on Imap worker thread,
            ' it cannot update controls on the form or otherwise access UI.
            ' The only UI-related action permitted in worker thread is
            ' displaying a message box (MessageBox.Show).
            ' Any updates of UI must take place on message loop thread.
            Private Sub SearchCallback(ByVal result As IAsyncResult)
            	Dim imp As Imap = CType(result.AsyncState, Imap)

            	' To end search and display results on UI thread, we use
            	' Form.Invoke passing Imap instance as a parameter.
            	Invoke(New UpdateUIDelegate(AddressOf UpdateUI), New Object() {imp})

            	' See comment in UpdateUI.
            	Try
            		imp.Disconnect()
            	Catch e As MailBeeException
            		MessageBox.Show(e.Message)
            	End Try
            End Sub

            ' The actual code.
            Private Sub Form1_Load(ByVal sender As Object, ByVal e As System.EventArgs)
            	Dim imp As New Imap

            	' Connect to the server, login and select inbox.
            	imp.Connect("imap4.mail.server.com")
            	imp.Login("jdoe@server.com", "secret")
            	imp.SelectFolder("Inbox")

            	' Start async search of messages received since yesterday.
            	imp.BeginSearch(False, "SINCE """ & _
            		ImapUtils.GetImapDateString(DateTime.Today.AddDays(-1)) & _
            		"""", Nothing, New AsyncCallback(AddressOf SearchCallback), imp)

            	' Note: we do not wait until search is finished here.
            	' Subsequent code calls will be made in SearchCallback.
            End Sub

This sample performs sorted search in the inbox and downgrades to the regular search if SORT capability is not supported by the server.

            using System;
            using MailBee;
            using MailBee.ImapMail;

            class Sample
            {
            	static void Main(string[] args)
            	{
            		Imap imp = new Imap();

            		// Connect to the server, login and select inbox.
            		imp.Connect("mail.company.com");
            		imp.Login("jdoe@company.com", "secret");
            		imp.SelectFolder("INBOX");

            		// Set to true if SORT extension is available.
            		bool sortAvail = (imp.GetExtension("SORT") != null);

            		// If SORT is supported, order messages by FROM field first (primary sort condition)
            		// and then order each group of messages coming from the same FROM by arrival date
            		// in descending order (secondary sort condition). If SORT is not supported, orderBy
            		// criteria will not be set and SortedSearch will downgrade to the regular Search.
            		string orderByCriteria = sortAvail ? "FROM REVERSE ARRIVAL" : null;

            		// Get UIDs of all messages in the inbox, sorted accordingly orderByCriteria.
            		UidCollection uids = (UidCollection)imp.SortedSearch(true, "ALL", null, orderByCriteria);

            		Console.WriteLine("SORT capability is " + (!sortAvail ? "NOT " : "") + "available");
            		Console.WriteLine("UIDs found: " + uids.ToString());

            		// Disconnect from the server.
            		imp.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.ImapMail

            Class Sample
            	Shared Sub Main(ByVal args() As String)
            		Dim imp As Imap = New Imap

            		' Connect to the server, login and select inbox.
            		imp.Connect("mail.company.com")
            		imp.Login("jdoe@company.com", "secret")
            		imp.SelectFolder("INBOX")

            		' Set to true if SORT extension is available.
            		Dim sortAvail As Boolean = (Not imp.GetExtension("SORT") Is Nothing)

            		' If SORT is supported, order messages by FROM field first (primary sort condition)
            		' and then order each group of messages coming from the same FROM by arrival date
            		' in descending order (secondary sort condition). If SORT is not supported, orderBy
            		' criteria will not be set and SortedSearch will downgrade to the regular Search.
            		Dim orderByCriteria As String = IIf(sortAvail, "FROM REVERSE ARRIVAL", Nothing)

            		' Get UIDs of all messages in the inbox, sorted accordingly orderByCriteria.
            		Dim uids As UidCollection = CType(imp.SortedSearch(True, "ALL", Nothing, orderByCriteria), UidCollection)

            		Console.WriteLine("SORT capability is " & IIf(Not sortAvail, "NOT ", "") & "available")
            		Console.WriteLine("UIDs found: " & uids.ToString())

            		' Disconnect from the server.
            		imp.Disconnect()
            	End Sub
            End Class

This console sample demonstrates asynchronous search of messages arrived into the Inbox folder since yesterday
            and sorting them by their Subject line.

            using System;
            using MailBee;
            using MailBee.ImapMail;

            class Sample
            {
            	// A callback function.
            	private static void SortedSearchCallback(IAsyncResult result)
            	{
            		// Extract values from state object.
            		Imap imp = (Imap)((object [])result.AsyncState)[0];
            		bool sortAvail = (bool)((object [])result.AsyncState)[1];

            		// Display the outcome.
            		Console.WriteLine("SORT capability is " + (!sortAvail ? "NOT " : "") + "available");
            		try
            		{
            			Console.WriteLine("UIDs found: " + ((UidCollection)imp.EndSortedSearch()).ToString());
            		}
            		catch (Exception e)
            		{
            			// Catching exception here is recommended. Because callback function is a separate
            			// thread, unhandled exception would silently terminate it without any message to
            			// the user (and it would be hard to understand what exactly happened).
            			Console.WriteLine("Exception occurred: " + e.Message);
            			Console.WriteLine(e.StackTrace);
            		}
            	}

            	// The actual code.
            	static void Main(string[] args)
            	{
            		Imap imp = new Imap();

            		// Connect to the server, login and select inbox.
            		imp.Connect("imap.domain.com");
            		imp.Login("jdoe", "secret");
            		imp.SelectFolder("INBOX");

            		// Set to true if SORT extension is available.
            		bool sortAvail = (imp.GetExtension("SORT") != null);

            		// If SORT is supported, order messages by SUBJECT field. If SORT is not supported,
            		// orderBy criteria will not be set and SortedSearch will downgrade to the regular Search.
            		string orderByCriteria = sortAvail ? "SUBJECT" : null;

            		// Initiate an asynchronous request for getting UIDs of messages received since yesterday,
            		// sorted accordingly orderByCriteria.
            		IAsyncResult ar = imp.BeginSortedSearch(true,
            			"SINCE \"" + ImapUtils.GetImapDateString(DateTime.Today.AddDays(-1)) + "\"",
            			null, orderByCriteria,
            			new AsyncCallback(SortedSearchCallback), new object[] {imp, sortAvail});

            		// Simulate some lengthy work here. At the same time,
            		// folder selection is executed on another thread.
            		System.Threading.Thread.Sleep(3000);

            		// If the folder selection attempt is still in progress,
            		// then wait until it's finished.
            		while (imp.IsBusy) ar.AsyncWaitHandle.WaitOne();

            		// Disconnect from the server.
            		imp.Disconnect();
            	}
            }

example.code:
using System;
using MailBee;
using MailBee.ImapMail;

class Sample
{
	// ErrorOccurred event handler.
	private static void OnErrorOccurred(object sender, ErrorEventArgs e)
	{
		if (e.Reason is MailBeeImapInvalidEnvelopeException)
		{
			MailBeeImapInvalidEnvelopeException invEnvEx =
				(MailBeeImapInvalidEnvelopeException)e.Reason;

			Console.WriteLine("Fetched data for message #" +
				invEnvEx.InvalidEnvelope.MessageNumber +
				" could not be completed parsed and will be ignored");
		}
	}

	static void Main(string[] args)
	{
		Imap imp = new Imap();

		imp.Connect("imap.somehost.com");

		imp.Login("jdoe@somehost.com", "secret");

		// Select Inbox folder.
		imp.SelectFolder("INBOX");

		// Subscribe to ErrorOccurred event to track invalid envelopes.
		// We could also subscribe before calling Login or Connect since
		// we check the type of occurred error in OnErrorOccurred handler.
		imp.ErrorOccurred += new ErrorEventHandler(OnErrorOccurred);

		EnvelopeCollection envs = null;

		// We'll collect UID of high priority messages in this collection. 
		UidCollection uids = new UidCollection();

		// If true, we'll search high priority messages using DownloadEnvelopes().
		// Otherwise, we'll use Search() method (much more effective). Thus, this
		// sample highlights both approaches.
		bool searchViaEnvelopes = true;

		// Download "X-Priority" and "X-MS-Mail-Priority" headers for each message.
		// We could have used Search() instead (just set searchViaEnvelopes=false).
		// Using Search() would be more effective. Imap.DownloadEnvelopes is used
		// here only to demonstrate how to get custom header fields from the server.
		if (searchViaEnvelopes)
		{
			envs = imp.DownloadEnvelopes(Imap.AllMessages, false,
				EnvelopeParts.Uid, 0, new string[] {"X-Priority", "X-MS-Mail-Priority"}, null);

			// For each message, determine if it's high priority.
			foreach (Envelope env in envs)
			{
				bool highPriority = false;

				if (env.IsValid)
				{
					string priority = env.ExtraHeaders["X-Priority"];
					if (priority != null && priority.Length > 0)
					{
						if (priority[0] == '1' || priority[0] == '2')
						{
							// X-Priority is High or Highest
							highPriority = true;
						}
					}
					else
					{
						priority = env.ExtraHeaders["X-MS-Mail-Priority"];
						if (priority != null && string.Compare(priority, "High", true) == 0)
						{
							// X-MS-Mail-Priority is High
							highPriority = true;
						}
					}
				}

				if (highPriority)
				{
					uids.Add(env.Uid);
				}
			}
		}
		else
		{
			// To search for High-Priority messages in the most effective way, set
			// searchViaEnvelopes=false in the beginning of this sample. Search() method
			// performs the same job as the code block above but produces much less
			// traffic and consumes less resources.
			uids = (UidCollection)imp.Search(true,"OR OR HEADER \"X-Priority\" \"1\" " +
				"HEADER \"X-Priority\" \"2\" HEADER \"X-MS-Mail-Priority\" \"High\"", null);
		}

		if (uids.Count > 0)
		{
			// Download envelope structures of high priority messages.
			envs = imp.DownloadEnvelopes(uids.ToString(), true,
				EnvelopeParts.BodyStructure, 0, null, null);

			// We'll collect UID of high priority messages with attachments in this collection.
			uids = new UidCollection();

			// For each message, determine if it has attachments.
			foreach (Envelope env in envs)
			{
				if (env.IsValid)
				{
					ImapBodyStructureCollection parts = env.BodyStructure.GetAllParts();
					foreach (ImapBodyStructure part in parts)
					{
						// Detect if this part is attachment.
						if ((part.Disposition != null &&
							part.Disposition.ToLower() == "attachment") ||
							(part.Filename != null &&
							part.Filename != string.Empty) ||
							(part.ContentType != null &&
							part.ContentType.ToLower() == "message/rfc822"))
						{
							uids.Add(env.Uid);
							break;
						}
					}
				}
			}

			if (uids.Count > 0)
			{
				// We could also call DownloadEntireMessages here. Internally,
				// DownloadEntireMessages would call DownloadEnvelopes with the same
				// parameters and returned MailMessageCollection where each MailMessage
				// would be taken from Envelope.MessagePreview.
				envs = imp.DownloadEnvelopes(uids.ToString(), true,
					EnvelopeParts.MessagePreview, -1, null, null);

				foreach (Envelope env in envs)
				{
					if (env.IsValid)
					{
						// Save all attachments. Note: if the folder already contains
						// attachment with the given name, MailBee will generate unique name
						// (e.g. pic.gif will be saved as pic[1].gif if pic.gif already exists
						// in the target folder.
						env.MessagePreview.Attachments.SaveAll(@"C:\Temp");
					}
				}
			}
		}

		// Disconnect from the server.
		imp.Disconnect();
	}
}

member.example:
For each message in the Inbox, this sample downloads the message preview (the message header section + first 1000 bytes of the body), the body structure
(to correctly determine if the message has attachments), and the date of receiving the message by the server.

using System;
using MailBee;
using MailBee.ImapMail;
using MailBee.Mime;

class Sample
{
	static void Main(string[] args)
	{
		Imap imp = new Imap();

		imp.Connect("mail.company.com");

		imp.Login("jdoe", "secret");

		// Select Inbox folder.
		imp.SelectFolder("Inbox");

		// Download body structure (header + 1000 bytes of the body), message preview,
		// and the date assigned to the message by the server (usually, it's the date
		// when the message was received by the server).
		EnvelopeCollection envs = imp.DownloadEnvelopes(Imap.AllMessages, false,
			EnvelopeParts.BodyStructure | EnvelopeParts.MessagePreview | EnvelopeParts.InternalDate,
			1000);

		foreach (Envelope env in envs)
		{
			Console.WriteLine("Message #" + env.MessageNumber + " info:");
			if (env.IsValid)
			{
				// Tell MailBee to create plain-text version of HTML message
				// if plain-text version is not available in the message.
				// It's important to set this before the message gets parsed
				// (which occurs automatically when any element of the message
				// is accessed though MailMessage object properties or methods).
				// If you change HtmlToPlainMode after the message has already been parsed,
				// call MailMessage.Parser.Apply to reparse the message with new settings.
				env.MessagePreview.Parser.HtmlToPlainMode = HtmlToPlainAutoConvert.IfNoPlain;

				Console.WriteLine("From: " + env.MessagePreview.From.ToString());
				Console.WriteLine("To: " + env.MessagePreview.To.ToString());
				Console.WriteLine("Subject: " + env.MessagePreview.Subject);
				Console.WriteLine("Composed at: " + env.MessagePreview.Date);

				// We use DateReceived from INTERNALDATE item of FETCH response.
				// We could have also used env.MessagePreview.DateReceived instead
				// (it's obtained from "Received:" headers of the message).
				// However, env.MessagePreview.DateReceived is DateTime.MinValue
				// when the message does not contain "Received:" headers.
				// INTERNALDATE, on other hand, is always available in IMAP4.
				// MailMessage.DateReceived still makes sense because INTERNALDATE
				// is not available in POP3, and examining "Received:" headers is
				// the only way of getting this info when using POP3 protocol.
				Console.WriteLine("Received at: " + env.DateReceived);

				if (env.MessagePreview.Priority >= MailPriority.High)
				{
					Console.WriteLine("The message is high priority");
				}

				// Determine if the message has attachments.
				bool hasAttachments = false;
				ImapBodyStructureCollection parts = env.BodyStructure.GetAllParts();
				foreach (ImapBodyStructure part in parts)
				{
					// Detect if this part is attachment.
					if ((part.Disposition != null && part.Disposition.ToLower() == "attachment") ||
						(part.Filename != null && part.Filename != string.Empty) ||
						(part.ContentType != null && part.ContentType.ToLower() == "message/rfc822"))
					{
						hasAttachments = true;
						break;
					}
				}
				if (hasAttachments)
				{
					Console.WriteLine("The message has attachments.");
				}

				// Display first 250 characters of the text body. Note that we requested
				// 1000 bytes but now display only 250 characters. We request more than
				// we display because of:
				// - body section of MIME message may start with a block of additional
				// information which is not a part of text body. Usually, the length
				// of this block is 100-300 bytes.
				// - non-ASCII characters may occupy more than one byte.
				// In some cases, reading 1000 bytes may still be not enough to reach the
				// text body (for instance, if the message contains attachment which preceeds
				// the text body in the message source). In such rare cases, the body preview
				// will not be available (or, the developer may request more than 1000 bytes
				// of the body and thus increase network traffic). 
				string bodyPreview = env.MessagePreview.BodyPlainText;
				if (bodyPreview.Length > 250)
				{
					bodyPreview = bodyPreview.Substring(0, 250);
				}
				Console.WriteLine();
				Console.WriteLine(bodyPreview);
			}
			else
			{
				Console.WriteLine("FETCH response contains some invalid data.");
			}
			Console.WriteLine("============================================================");
		}

		// Disconnect from the server.
		imp.Disconnect();
	}
}


