This sample attempts to gets the size of the currently used account storage using QUOTA extension.
            If QUOTA is not supported by the server, the sample selects all selectable folders in the account one by another,
            and calls  for each folder.

            using System;
            using MailBee;
            using MailBee.ImapMail;

            class Sample
            {
            	static void Main(string[] args)
            	{
            		Imap imp = new Imap();

            		// Connect to the server and log in the account.
            		imp.Connect("mail.company.com");
            		imp.Login("jdoe@company.com", "secret");

            		long accountStorageUsedSize = -1;

            		if (imp.GetExtension("QUOTA") != null)
            		{
            			// This may still return -1 if the QUOTA is supported
            			// in general but storage quota in bytes is not.
            			accountStorageUsedSize = imp.GetAccountQuota().CurrentStorageSize;
            		}

            		if (accountStorageUsedSize < 0)
            		{
            			// Fall back to a series of SelectFolder/GetFolderSize calls.
            			accountStorageUsedSize = 0;
            			FolderCollection folders = imp.DownloadFolders();

            			foreach (Folder fold in folders)
            			{
            				// For each selectable folder, select it and
            				// determine the total size of all messages.
            				if ((fold.Flags & FolderFlags.Noselect) == 0)
            				{
            					imp.SelectFolder(fold.Name);
            					accountStorageUsedSize += imp.GetFolderSize();
            				}
            			}
            		}

            		Console.WriteLine("Currently used account storage size is " +
            			accountStorageUsedSize + " bytes");

            		// Disconnect from the server.
            		imp.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.ImapMail

            Module Sample
            	Sub Main(ByVal args As String())
            		Dim imp As New Imap

            		' Connect to the server and log in the account.
            		imp.Connect("mail.domain.com")
            		imp.Login("jdoe", "secret")

            		Dim accountStorageUsedSize As Long = -1

            		If Not imp.GetExtension("QUOTA") Is Nothing Then
            			' This may still return -1 if the QUOTA is supported
            			' in general but storage quota in bytes is not.
            			accountStorageUsedSize = imp.GetAccountQuota().CurrentStorageSize
            		End If

            		If accountStorageUsedSize < 0 Then
            			' Fall back to a series of SelectFolder/GetFolderSize calls.
            			accountStorageUsedSize = 0
            			Dim folders As FolderCollection = imp.DownloadFolders()

            			For Each fold As Folder In folders
            				' For each selectable folder, select it and
            				' determine the total size of all messages.
            				If (fold.Flags And FolderFlags.Noselect) = 0 Then
            					imp.SelectFolder(fold.Name)
            					accountStorageUsedSize += imp.GetFolderSize()
            				End If
            			Next
            		End If

            		Console.WriteLine("Currently used account storage size is " & _
            			accountStorageUsedSize & " bytes")

            		' Disconnect from the server.
            		imp.Disconnect()
            	End Sub
            End Module

This sample loads the mail message from a file, and then uploads it to the Inbox folder on the server specifying "12-Apr-2006 19:30:00 +0100"
            as date of receiving the message by the server (INTERNALDATE), and setting "\Seen" and "\Flagged" flags (the server will also set "\Recent" flag). Finally,
            the UID value assigned to the message is displayed in the console. If UIDPLUS extension is supported,
            it's used. Otherwise, the application obtains the UID value downloading the status of Inbox folder prior to making upload.

            using System;
            using MailBee;
            using MailBee.ImapMail;
            using MailBee.Mime;

            class Sample
            {
            	static void Main(string[] args)
            	{
            		Imap imp = new Imap();

            		// Connect to the server and log in the account.
            		imp.Connect("mail.server.com");
            		imp.Login("jdoe", "secret");

            		// Load the message from C:\Temp\message.eml file.
            		MailMessage msg = new MailMessage();
            		msg.LoadMessage(@"C:\Temp\message.eml");

            		// Prepare the object that will receive upload results.
            		UidPlusResult res = new UidPlusResult();

            		long uid = 0;

            		if (imp.GetExtension("UIDPLUS") == null)
            		{
            			FolderStatus status = imp.GetFolderStatus("Inbox");
            			uid = status.UidNext;
            		}

            		// Upload the message and fill res with upload results.
            		imp.UploadMessage(msg, "Inbox",
            			MessageFlagSet.SystemFlagsToString(
            			SystemMessageFlags.Seen | SystemMessageFlags.Flagged),
            			"12-Apr-2006 19:30:00 +0100", true, res);

            		if (res.IsSupported)
            		{
            			Console.WriteLine("UID of the uploaded message is " +
            				res.DestUidString + ", UIDPLUS supported.");
            		}
            		else
            		{
            			Console.WriteLine("UID of the uploaded message is " +
            				uid.ToString() + ", UIDPLUS not supported.");
            		}

            		// Disconnect from the server.
            		imp.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.ImapMail
            Imports MailBee.Mime

            Module Sample
            	Sub Main()
            		Dim imp As New Imap

            		' Connect to the server, login and select inbox.
            		imp.Connect("mail.domain.com")
            		imp.Login("jdoe", "secret")

            		' Load the message from C:\Temp\message.eml file.
            		Dim msg As New MailMessage
            		msg.LoadMessage("C:\Temp\message.eml")

            		' Prepare the object that will receive upload results.
            		Dim res As New UidPlusResult

            		Dim uid As Long = 0

            		If Not imp.GetExtension("UIDPLUS") Is Nothing Then
            			Dim status As FolderStatus = imp.GetFolderStatus("Inbox")
            			uid = status.UidNext
            		End If

            		' Upload the message and fill res with upload results.
            		imp.UploadMessage(msg, "Inbox", _
            			MessageFlagSet.SystemFlagsToString( _
            			SystemMessageFlags.Seen Or SystemMessageFlags.Flagged), _
            			"12-Apr-2006 19:30:00 +0100", True, res)

            		If res.IsSupported Then
            			Console.WriteLine("UID of the uploaded message is " & _
            			 res.DestUidString & ", UIDPLUS supported.")
            		Else
            			Console.WriteLine("UID of the uploaded message is " & _
            			 uid.ToString() & ", UIDPLUS not supported.")
            		End If

            		' Disconnect from the server.
            		imp.Disconnect()
            	End Sub
