example.code:
using System;
using MailBee;
using MailBee.ImapMail;

class Sample
{
	static void Main(string[] args)
	{
		Imap imp = new Imap();

		// Set where to save log file. However, we do
		// not enable logging here. We'll enable it
		// later for a single moment of setting flags.
		imp.Log.Filename = @"C:\Temp\log.txt";

		// Connect to the server, login and select inbox.
		imp.Connect("imap.somedomain.com");
		imp.Login("jdoe", "secret");
		imp.SelectFolder("INBOX");

		if (imp.MessageCount > 0)
		{
			// Enable logging into a file to track
			// the status of setting flags.
			imp.Log.Enabled = true;

			// Replace all flags of the last message with "$Spam" flag.
			// The developer can then take a look at C:\Temp\log.txt file
			// to find out if untagged "* FETCH (FLAGS)" response to
			// STORE command contains "$Spam" element. If it does, the
			// server indeed applied this flag to the message.
			imp.SetMessageFlags(imp.MessageCount.ToString(), false,
				"$Spam", MessageFlagAction.Replace, false);

			// Disable logging again. Thus, the log file will be pretty
			// small, and the developer will be able to read it easily.
			imp.Log.Enabled = false;

			// Demonstrate alternative way of analyzing FETCH responses.
			string[] flagResponses = imp.GetServerResponses("FETCH");
			foreach (string response in flagResponses)
			{
				Console.WriteLine(response);
			}
		}
		else
		{
			Console.WriteLine("The inbox is empty");
		}

		// Disconnect from the server.
		imp.Disconnect();
	}
}

member.example:
This sample undeletes all messages marked as deleted in the inbox folder.

            using System;
            using MailBee;
            using MailBee.ImapMail;

            class Sample
            {
            	static void Main(string[] args)
            	{
            		Imap imp = new Imap();

            		// Connect to the server, login and select inbox.
            		imp.Connect("imap4.server.com");
            		imp.Login("jdoe@server.com", "secret");
            		imp.SelectFolder("INBOX");

            		// Get UIDs of deleted messages. We could have also skipped this
            		// stage and just tell the server to remove \Deleted flag for
            		// all messages (even for those message which do not have this
            		// flag set - such messages won't be affected). We call Search()
            		// method and then consume its results in SetMessageFlags() just
            		// for purposes of illustration.
            		UidCollection uids = (UidCollection)imp.Search(true, "DELETED", null);

            		if (uids.Count > 0)
            		{
            			// Remove \Deleted flag from the deleted messages.
            			imp.SetMessageFlags(uids.ToString(), true,
            				SystemMessageFlags.Deleted, MessageFlagAction.Remove);
            		}

            		// Disconnect from the server.
            		imp.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.ImapMail

            Module Sample
            	Sub Main(ByVal args As String())
            		Dim imp As New Imap

            		' Connect to the server, login and select inbox.
            		imp.Connect("imap4.server.com")
            		imp.Login("jdoe@server.com", "secret")
            		imp.SelectFolder("INBOX")

            		' Get UIDs of deleted messages. We could have also skipped this
            		' stage and just tell the server to remove \Deleted flag for
            		' all messages (even for those message which do not have this
            		' flag set - such messages won't be affected). We call Search()
            		' method and then consume its results in SetMessageFlags() just
            		' for purposes of illustration.
            		Dim uids As UidCollection = CType(imp.Search(True, "DELETED", Nothing), UidCollection)

            		If uids.Count > 0 Then
            			' Remove \Deleted flag from the deleted messages.
            			imp.SetMessageFlags(uids.ToString(), True, SystemMessageFlags.Deleted, MessageFlagAction.Remove)
            		End If

            		' Disconnect from the server.
            		imp.Disconnect()
            	End Sub
            End Module

example.code:
// To use the code below, import MailBee namespaces at the top of your code.
using MailBee;
using MailBee.ImapMail;

// Put the code below inside your class.

bool finished;
bool started = false;

// A callback function. Since it's called on Imap worker thread,
// it cannot display any messages or otherwise access UI.
// Any updates of UI must take place on message loop thread.
// See the sample in BeginSearch() topic on how to update UI in
// such scenario.
private void SetMessageFlagsCallback(IAsyncResult result)
{
	Imap imp = (Imap)result.AsyncState;

	// It's important to handle exceptions in worker threads. Otherwise,
	// the worker thread could silently die and the application would
	// have never known this had happened.
	try
	{
		imp.EndSetMessageFlags();
		imp.Expunge();
	}
	catch (MailBeeException e)
	{
		MessageBox.Show(e.Message);
	}
	finally
	{
		finished = true;
	}
}

// Add button1 on the form to make this sample working.
private void button1_Click(object sender, System.EventArgs e)
{
	if (started)
	{
		MessageBox.Show("The operation is already in progress");
	}
	else
	{
		started = true;
	}

	finished = false;

	Imap imp = new Imap();

	// Connect to the server, login and select inbox.
	imp.Connect("mail.company.com");
	imp.Login("jdoe", "secret");
	imp.SelectFolder("Inbox");

	// Asynchronously mark the first message as deleted. Instead of
	// MessageFlagSet.SystemFlagsToString(SystemMessageFlags.Deleted),
	// we could simply use "\Deleted" (@"\Deleted" in C#).
	imp.BeginSetMessageFlags("1", false,
		MessageFlagSet.SystemFlagsToString(SystemMessageFlags.Deleted),
		MessageFlagAction.Add, true,
		new AsyncCallback(SetMessageFlagsCallback), imp);

	// Wait until completion via periodic polling
	// of the completion status.
	while (!finished)
	{
		System.Threading.Thread.Sleep(1);

		// It's not necessary to call DoEvents here since we do not
		// handle any MailBee events in this sample. However, if we did,
		// we would have needed this call to let the events be processed.
		// Alternatively, we could have set imp.RaiseEventsViaMessageLoop
		// to false and use imp.Wait() instead of Thread.Sleep() and
		// Application.DoEvents(). If using imp.Wait(), we would not need
		// to prevent this routine from being called again while it's
		// already being executed since imp.Wait() blocks UI and it would
		// be impossible to click a button until imp.Wait() completes.
		Application.DoEvents();
	}

	// Disconnect from the server.
	imp.Disconnect();

	started = false;
}

member.example:
This sample copies last 3 messages from the inbox folder to the Sent folder (it's assumed the Sent folder already exists and the inbox
            contains at least 3 messages). The UIDs assigned to the copied messages in the destination folder are displayed if UIDPLUS capability is supported by the server.

            using System;
            using MailBee;
            using MailBee.ImapMail;

            class Sample
            {
            	static void Main(string[] args)
            	{
            		Imap imp = new Imap();

            		// Connect to the server, login and select inbox.
            		imp.Connect("mail.company.com");
            		imp.Login("jdoe@company.com", "secret");
            		imp.SelectFolder("INBOX");

            		// Prepare the object that will receive copying results.
            		UidPlusResult res = new UidPlusResult();

            		string range = (imp.MessageCount - 2).ToString() + ":*";

            		// Copy the messages into "Sent" and fill res with copying results.
            		imp.CopyMessages(range, false, "Sent", res);

            		if (res.IsSupported)
            		{
            			Console.WriteLine("UIDs assigned to the copied messages are " +
            				res.DestUidString);
            		}
            		else
            		{
            			Console.WriteLine("Sorry, UIDPLUS is not supported by the server");
            		}

            		// Disconnect from the server.
            		imp.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.ImapMail

            Module Sample
            	Sub Main(ByVal args As String())
            		Dim imp As New Imap

            		' Connect to the server, login and select inbox.
            		imp.Connect("mail.company.com")
            		imp.Login("jdoe@company.com", "secret")
            		imp.SelectFolder("INBOX")

            		' Prepare the object that will receive copying results.
            		Dim res As New UidPlusResult
            		Dim range As String = (imp.MessageCount - 2).ToString() & ":*"

            		' Copy the messages into "Sent" and fill res with copying results.
            		imp.CopyMessages(range, False, "Sent", res)

            		If res.IsSupported Then
            			Console.WriteLine("UIDs assigned to the copied messages are " & _
            				res.DestUidString)
            		Else
            			Console.WriteLine("Sorry, UIDPLUS is not supported by the server")
            		End If

            		' Disconnect from the server.
            		imp.Disconnect()
            	End Sub
            End Module

