This sample calculates all incoming traffic from the server during the IMAP4 session,
            and prints the result into console.

            using System;
            using MailBee;
            using MailBee.ImapMail;
            using MailBee.Mime;

            class Sample
            {
            	// Total bytes received counter.
            	private static int _totalBytes = 0;

            	// LowLevelDataReceived event handler.
            	private static void OnLowLevelDataReceived(object sender, DataTransferEventArgs e)
            	{
            		// Increment the counter.
            		_totalBytes += e.Data.Length;
            	}

            	// The actual code.
            	static void Main(string[] args)
            	{
            		Imap imp = new Imap();

            		// Subscribe to the LowLevelDataReceived event.
            		imp.LowLevelDataReceived += new DataTransferEventHandler(OnLowLevelDataReceived);

            		// Do something which would produce some network traffic.
            		imp.Connect("mail.domain.com");
            		imp.Login("jdoe", "secret");
            		imp.SelectFolder("Inbox");
            		MailMessageCollection msgs = imp.DownloadMessageHeaders(Imap.AllMessages, false);
            		imp.Disconnect();

            		// Print the total number of bytes previously received from the server.
            		Console.WriteLine(_totalBytes + " bytes received in all");
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.ImapMail
            Imports MailBee.Mime

            Module Sample
            	' Total bytes received counter.
            	Dim _totalBytes As Integer = 0

            	' LowLevelDataReceived event handler.
            	Private Sub OnLowLevelDataReceived(ByVal sender As Object, ByVal e As DataTransferEventArgs)
            		' Increment the counter.
            		_totalBytes += e.Data.Length
            	End Sub

            	' The actual code.
            	Sub Main(ByVal args As String())
            		Dim imp As New Imap

            		' Subscribe to the LowLevelDataReceived event.
            		AddHandler imp.LowLevelDataReceived, AddressOf OnLowLevelDataReceived

            		' Do something which would produce some network traffic.
            		imp.Connect("mail.domain.com")
            		imp.Login("jdoe", "secret")
            		imp.SelectFolder("Inbox")
            		Dim msgs As MailMessageCollection = imp.DownloadMessageHeaders(Imap.AllMessages, False)
            		imp.Disconnect()

            		' Print the total number of bytes previously received from the server.
            		Console.WriteLine(_totalBytes & " bytes received in all")
            	End Sub
            End Module

