            	Private Sub OnTlsStarted(ByVal sender As Object, ByVal e As TlsStartedEventArgs)
            		' This will happen during Login method execution.
            		Console.WriteLine("TLS/SSL session started.")
            	End Sub

            	' The actual code.
            	Sub Main(ByVal args As String())
            		Dim imp As New Imap

            		' Subscribe to TlsStarted event.
            		AddHandler imp.TlsStarted, AddressOf OnTlsStarted

            		' Notify MailBee it should start TLS/SSL session when appropriate.
            		' The connection is made to the regular port so that STARTTLS command
            		' will be used to start TLS/SSL session.
            		imp.SslMode = SslStartupMode.UseStartTls

            		imp.Connect("imap.company.com")

            		Console.WriteLine("Connected to the server. Will login now...")

            		' TLS/SSL negotiation will take place here. Thus, user credentials
            		' will be sent to the mail server already under secure TLS/SSL layer.
            		imp.Login("jdoe", "secret")

            		Console.WriteLine("Logged in successfully.")

            		imp.Disconnect()
            	End Sub
            End Module

This sample connects to the IMAP4 server and logs in the user account. The sample
            also checks if the  status was set to true after calling .
            This is required to make sure the client has not already been authenticated via PREAUTH.

            using System;
            using MailBee;
            using MailBee.ImapMail;

            class Sample
            {
            	private static string accountName = "Preauthenticated user";

            	// LoggedIn event handler.
            	private static void OnLoggedIn(object sender, LoggedInEventArgs e)
            	{
            		Console.WriteLine("Logged in as " + accountName);
            	}

            	// The actual code.
            	static void Main(string[] args)
            	{
            		Imap imp = new Imap();

            		// Subscribe to the LoggedIn event.
            		imp.LoggedIn += new LoggedInEventHandler(OnLoggedIn);

            		// Connect to the server. It's possible the server will automatically
            		// authenticate us in some cases. In other samples, we do not check
            		// if the auto-login occurred but real-world applications should check this.
            		imp.Connect("imap4.somedomain.com");

            		if (!imp.IsLoggedIn)
            		{
            			// Authenticate the user via Login()
            			accountName = "jdoe";
            			imp.Login(accountName, "secret");
            		}

            		imp.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.ImapMail

            Module Sample
            	Dim accountName As String = "Preauthenticated user"

            	' LoggedIn event handler.
            	Private Sub OnLoggedIn(ByVal sender As Object, ByVal e As LoggedInEventArgs)
            		Console.WriteLine("Logged in as " & accountName)
            	End Sub

            	' The actual code.
            	Sub Main(ByVal args As String())
            		Dim imp As New Imap

            		' Subscribe to the LoggedIn event.
            		AddHandler imp.LoggedIn, AddressOf OnLoggedIn

            		' Connect to the server. It's possible the server will automatically
            		' authenticate us in some cases. In other samples, we do not check
            		' if the auto-login occurred but real-world applications should check this.
            		imp.Connect("imap4.somedomain.com")

            		If Not imp.IsLoggedIn Then
            			' Authenticate the user via Login()
            			accountName = "jdoe"
            			imp.Login(accountName, "secret")
            		End If

            		imp.Disconnect()
            	End Sub
            End Module

This console sample displays the total size (via ) and the
            individual sizes (via ) of all messages in the folder.
            The logging into a file is enabled. The sample demonstrates  is raised
            by  method only (despite the fact that both 
            and  calls make the server to produce the same FETCH responses, the log file shows this).

            using System;
            using MailBee;
            using MailBee.ImapMail;

            class Sample
            {
            	// EnvelopeDownloaded event handler.
            	private static void OnEnvelopeDownloaded(object sender,
            		ImapEnvelopeDownloadedEventArgs e)
            	{
            		Console.WriteLine("Envelope (size and UID) of message #" +
            			e.MessageNumber + " downloaded");
            	}

            	// The actual code.
            	static void Main(string[] args)
            	{
            		Imap imp = new Imap();

            		// Enable logging to demonstrate GetFolderSize() and
            		// DownloadEnvelopes(Imap.AllMessages, EnvelopeParts.Rfc822Size, 0)
            		// produce the same requests and responses.
            		imp.Log.Filename = @"C:\Temp\log.txt";
            		imp.Log.Enabled = true;
            		imp.Log.Clear();

            		// Subscribe to the EnvelopeDownloaded event.
            		imp.EnvelopeDownloaded +=
            			new ImapEnvelopeDownloadedEventHandler(OnEnvelopeDownloaded);

            		// Connect to the server, login and select inbox.
            		imp.Connect("mail.domain.com");
            		imp.Login("jdoe", "secret");
            		imp.SelectFolder("INBOX");

            		// Get the total size of all messages in the folder, and display it.
            		Console.WriteLine("The total size of all messages in Inbox is: " +
            			imp.GetFolderSize() + " bytes");

            		// Get the size of each message in the folder...
            		EnvelopeCollection envs = imp.DownloadEnvelopes(Imap.AllMessages, false,
            			EnvelopeParts.Rfc822Size, 0);

            		// ... and display it.
            		foreach (Envelope env in envs)
            		{
            			Console.WriteLine("Size of message #" + env.MessageNumber + " is " +
            				env.Size + " bytes");
            		}

            		imp.Disconnect();
            	}
            }


            Imports System
            Imports MailBee
            Imports MailBee.ImapMail

