This sample loads the message from .EML file, extracts the encapsulated message, and displays
            all headers of the encapsulated message.

            // To use the code below, import MailBee namespaces at the top of your code.
            using MailBee;
            using MailBee.Mime;

            // The actual code (put it into a method of your class)

            // Load the message from file.
            MailMessage msg = new MailMessage();
            msg.LoadMessage(@"C:\Docs\TestMail.eml");

            MailMessage attachedMsg = null;

            // For every attachment...
            foreach (Attachment attach in msg.Attachments)
            {
            	// ...get the inner e-mail from the attachment.
            	attachedMsg = attach.GetEncapsulatedMessage();

            	// When there is any inner e-mail...
            	if (attachedMsg != null)
            	{
            		// ...show the header section of that e-mail.
            		Console.WriteLine(attachedMsg.RawHeader);
            		Console.WriteLine();
            	}
            }


            ' To use the code below, import MailBee namespaces at the top of your code.
            Imports MailBee
            Imports MailBee.Mime

            ' The actual code (put it into a method of your class)

            ' Load the message from file.
            Dim msg As New MailMessage
            msg.LoadMessage("C:\Docs\TestMail.eml")

            Dim attachedMsg As MailMessage = Nothing

            ' For every attachment...
            For Each attach As Attachment In msg.Attachments
            	' ...get the inner e-mail from the attachment.
            	attachedMsg = attach.GetEncapsulatedMessage()

            	' When there is any inner e-mail...
            	If Not attachedMsg Is Nothing Then
            		' ...show the header section of that e-mail.
            		Console.WriteLine(attachedMsg.RawHeader)
            		Console.WriteLine()
            	End If
            Next

