This sample demonstrates removing of images from an HTML source and replacing of BR tags
with HR tags.

// To use the code below, import these namespaces at the top of your code.
using System.IO;
using System.Net;
using MailBee;
using MailBee.Html;

class Sample
{
	// The delegate is used for processing of the HTML source.
	// All IMG tags will be removed and all BR tags will be replaced with HR tags.
	static bool ProcessDelegate(Element elem, Rule rule)
	{
		switch (elem.TagName.ToLower())
		{
			case "img":
				elem.Remove();
				break;
			case "br":
				elem.TagName = "hr";
				return false;
		}
		return true;
	}

	static void Main(string[] args)
	{
		// Create a new stream and load the HTML document into it.
		WebRequest myWebRequest = WebRequest.Create("http://www.afterlogic.com"); 
		WebResponse myWebResponse = myWebRequest.GetResponse(); 
		Stream receivingStream = myWebResponse.GetResponseStream();

		Processor htmlProcessor = new Processor();

		// Load the stream into Processor instance.
		htmlProcessor.LoadFromStream(receivingStream, Global.DefaultEncoding);

		ProcessElementDelegate del = new ProcessElementDelegate(ProcessDelegate);

		RuleSet rules = new RuleSet();

		// Add HTML processing rules.
		rules.AddTagProcessingCondition("img", null);
		rules.AddTagProcessingCondition("br", null);

		// Process the delegate and the rules.
		htmlProcessor.Dom.Process(rules, del);

		// Save the resulting HTML to disk.
		FileStream outStream = new FileStream(@"C:\afterlogic.html", FileMode.Create, FileAccess.Write, FileShare.None);

		htmlProcessor.SaveToStream(outStream, Global.DefaultEncoding);
		outStream.Close();
	}
}


' To use the code below, import these namespaces at the top of your code.
Imports System.IO
Imports System.Net
Imports MailBee
Imports MailBee.Html

Module Sample
	' The delegate is used for processing of the HTML source.
	' All IMG tags will be removed and all BR tags will be replaced with HR tags.
	Function ProcessDelegate(ByVal elem As Element, ByVal rule As Rule) As Boolean
		Select Case (elem.TagName.ToLower())
			Case "img"
				elem.Remove()
			Case "br"
				elem.TagName = "hr"
				Return False
		End Select
		Return True
	End Function

	Sub Main()
		' Create a new stream and load the HTML document into it.
		Dim myWebRequest As WebRequest = WebRequest.Create("http://www.afterlogic.com")
		Dim myWebResponse As WebResponse = myWebRequest.GetResponse()
		Dim receivingStream As Stream = myWebResponse.GetResponseStream()

		Dim htmlProcessor As New Processor

		' Load the stream into Processor instance.
		htmlProcessor.LoadFromStream(receivingStream, Global.DefaultEncoding)

		Dim del As ProcessElementDelegate = AddressOf ProcessDelegate

		Dim rules As New RuleSet

		' Add HTML processing rules.
		rules.AddTagProcessingCondition("img", Nothing)
		rules.AddTagProcessingCondition("br", Nothing)

		' Process the delegate and the rules.
		htmlProcessor.Dom.Process(rules, del)

		' Save the resulting HTML to disk.
		Dim outStream As FileStream = New FileStream("C:\afterlogic.html", FileMode.Create, FileAccess.Write, FileShare.None)

		htmlProcessor.SaveToStream(outStream, Global.DefaultEncoding)
		outStream.Close()
	End Sub
End Module

This sample change the background color of the HTML page to black.

// To use the code below, import these namespaces at the top of your code.
using System;
using System.IO;
using System.Net;
using MailBee;
using MailBee.Html;

class Sample
{
	static void Main(string[] args)
	{
		// Create new stream with an HTML markup.
		WebRequest myWebRequest = WebRequest.Create("http://www.afterlogic.com"); 
		WebResponse myWebResponse = myWebRequest.GetResponse(); 
		Stream receivingStream = myWebResponse.GetResponseStream();

		Processor htmlProcessor = new Processor();

		htmlProcessor.LoadFromStream(receivingStream, Global.DefaultEncoding);

		RuleSet rules = new RuleSet();

		// Create new rule for processing.
		TagAttributeCollection attributes = new TagAttributeCollection();
		TagAttribute attribute = new TagAttribute();
		attribute.Definition = "bgcolor = 'black'";

		attributes.Add(attribute);

		rules.AddTagRemovalRule("link", null);
		rules.AddTagProcessingRule("body", null, attributes, null, false);

		// Processing of the rule.
		Console.WriteLine(htmlProcessor.Dom.ProcessToString(rules, null));
	}
}


