           	Console.WriteLine(m.Subject)
           Next

This sample downloads essential information about the messages in the inbox (such as IMAP4 envelope,
date of receiving by the server, flags, and size), and its body structure. The messages themselves are not downloaded.
The body structure information is used to count attachments of the message (this approach is more reliable than downloading
message header and relying on  property which might not be 100% correct).
To notify user about particular envelopes which cannot be parsed,  event handler is used.

using System;
using MailBee;
using MailBee.ImapMail;

class Sample
{
	// ErrorOccurred event handler
	private static void OnErrorOccurred(object sender, ErrorEventArgs e)
	{
		// Filter only those errors which correspond to envelopes which cannot be parsed.
		MailBeeImapInvalidEnvelopeException ex =
			e.Reason as MailBeeImapInvalidEnvelopeException;

		if (ex != null)
		{
			Console.WriteLine("WARNING: IMAP response for message #" +
				ex.InvalidEnvelope.MessageNumber + " is incorrect.");
		}
}

	// The actual code
	static void Main(string[] args)
	{
		Imap imp = new Imap();

		// Attach ErrorOccurred event handler.
		imp.ErrorOccurred += new ErrorEventHandler(OnErrorOccurred);

		// Get to inbox.
		imp.Connect("imap.domain.com");
		imp.Login("jdoe", "secret");
		imp.SelectFolder("Inbox");

		// Get envelopes, body structures, message flags, etc for all messages in the inbox.
		EnvelopeCollection envelopes = imp.DownloadEnvelopes(
			Imap.AllMessages, false, EnvelopeParts.MailBeeEnvelope | EnvelopeParts.BodyStructure, 0);

		// Demonstrate that we can work with downloaded envelopes even in offline mode.
		imp.Disconnect();

		foreach (Envelope envelope in envelopes)
		{
			if (envelope.IsValid)
			{
				Console.WriteLine("Message #" + envelope.MessageNumber +
					", Received at: " + envelope.DateReceived + ", Created at: " + envelope.Date);
				Console.WriteLine("  Flags: " + envelope.Flags.ToString());
				Console.WriteLine("  From: " + envelope.From.ToString());
				Console.WriteLine("  To: " + envelope.To.ToString());
				Console.WriteLine("  Subject: " + envelope.Subject);
				System.Text.StringBuilder strBuffer = new System.Text.StringBuilder();
				ImapBodyStructureCollection parts = envelope.BodyStructure.GetAllParts();
				foreach (ImapBodyStructure part in parts)
				{
					// Detect if this part is attachment.
					if ((part.Disposition != null && part.Disposition.ToLower() == "attachment") ||
						(part.Filename != null && part.Filename != string.Empty) ||
						(part.ContentType != null && part.ContentType.ToLower() == "message/rfc822"))
					{
						string filename;
						if (part.Filename != null && part.Filename.Length > 0)
						{
							filename = part.Filename;
						}
						else
						{
							filename = "untitled";
						}

						// Important: Size property returns the length of part data in encoded form
						// (such as base64). There is no way to learn the exact length unless to
						// download the entire message. However, you may improve precision by examining
						// MailEncodingName property value. If it's "base64", decoded data is about 70%
						// in size of mail-encoded data. However, it's hard to estimate if it's
						// "quoted-printable" because decoded size/encoded size ratio highly depends on
						// the actual data contents in this case. Fortunately, "quoted-printable" is
						// quite rarely used for attachments. If MailEncodingName is "", "binary", "7bit",
						// or "8bit", then the data is not encoded, and Size property strictly matches
						// the actual data length.
						strBuffer.Append("[" + filename + " of approx. " + part.Size + " bytes]");
					}
				}
				if (strBuffer.Length > 0)
				{
					Console.WriteLine("  Attachments: " + strBuffer.ToString());
				}
				else
				{
					Console.WriteLine("  Attachments: none");
				}
			}
			else
			{
				Console.WriteLine("Could not parse envelope for message #" + envelope.MessageNumber);
			}
		}
	}
}


Imports System
Imports MailBee
Imports MailBee.ImapMail

Module Sample
	' ErrorOccurred event handler
	Private Sub OnErrorOccurred(ByVal sender As Object, ByVal e As ErrorEventArgs)
		' Filter only those errors which correspond to envelopes which cannot be parsed.
		Dim ex As MailBeeImapInvalidEnvelopeException = e.Reason

		If Not ex Is Nothing Then
			Console.WriteLine("WARNING: IMAP response for message #" & _
				ex.InvalidEnvelope.MessageNumber & " is incorrect.")
		End If
	End Sub

	' The actual code
	Sub Main(ByVal args As String())
		Dim imp As New Imap

		' Attach ErrorOccurred event handler.
		AddHandler imp.ErrorOccurred, AddressOf OnErrorOccurred

		' Get to inbox.
		imp.Connect("imap.domain.com")
		imp.Login("jdoe", "secret")
		imp.SelectFolder("Inbox")

		' Get envelopes, body structures, message flags, etc for all messages in the inbox.
		Dim envelopes As EnvelopeCollection

		envelopes = imp.DownloadEnvelopes(Imap.AllMessages, False, EnvelopeParts.MailBeeEnvelope _
			Or EnvelopeParts.BodyStructure, 0)

		' Demonstrate that we can work with downloaded envelopes even in offline mode.
		imp.Disconnect()

		For Each env As Envelope In envelopes
			If env.IsValid Then
				Console.WriteLine("Message #" & env.MessageNumber & ", Received at: " & _
					env.DateReceived & ", Created at: " & env.Date)
				Console.WriteLine(" Flags: " & env.Flags.ToString())
				Console.WriteLine(" From: " & env.From.ToString())
				Console.WriteLine(" To: " & env.To.ToString())
				Console.WriteLine(" Subject: " & env.Subject)

				Dim strBuffer As New System.Text.StringBuilder
				Dim parts As ImapBodyStructureCollection = env.BodyStructure.GetAllParts()

				For Each part As ImapBodyStructure In parts
					' Detect if this part is attachment.
					If (Not part.Disposition Is Nothing AndAlso part.Disposition.ToLower() = "attachment") OrElse _
						(Not part.Filename Is Nothing AndAlso part.Filename <> String.Empty) OrElse _
						(Not part.ContentType Is Nothing AndAlso part.ContentType.ToLower() = "message/rfc822") Then

						Dim filename As String

						If (Not part.Filename Is Nothing) And (part.Filename.Length > 0) Then
							filename = part.Filename
						Else
							filename = "untitled"
						End If

						' Important: Size property returns the length of part data in encoded form
						' (such as base64). There is no way to learn the exact length unless to
						' download the entire message. However, you may improve precision by examining
						' MailEncodingName property value. If it's "base64", decoded data is about 70%
						' in size of mail-encoded data. However, it's hard to estimate if it's
						' "quoted-printable" because decoded size/encoded size ratio highly depends on
						' the actual data contents in this case. Fortunately, "quoted-printable" is
						' quite rarely used for attachments. If MailEncodingName is "", "binary", "7bit",
						' or "8bit", then the data is not encoded, and Size property strictly matches
						' the actual data length.
						strBuffer.Append("[" & filename & " of approx. " & part.Size & " bytes]")
					End If
				Next

				If strBuffer.Length > 0 Then
					Console.WriteLine(" Attachments: " & strBuffer.ToString())
				Else
					Console.WriteLine(" Attachments: none")
				End If
			Else
				Console.WriteLine("Could not parse envelope for message #" & env.MessageNumber)
			End If
		Next
	End Sub
End Module

example.code:
using System;
using MailBee;
using MailBee.ImapMail;
using MailBee.Mime;

class Sample
{
	static void Main(string[] args)
	{
		Imap imp = new Imap();

		// Connect to the server, login and select inbox.
		imp.Connect("imap.company.com");
		imp.Login("jdoe", "secret");
		imp.SelectFolder("INBOX");

		// Download body structure.
		EnvelopeCollection envs = imp.DownloadEnvelopes(imp.MessageCount.ToString(), false,
			EnvelopeParts.BodyStructure, 0);

		// The list of FETCH keys to request from the server.
		System.Collections.ArrayList fetchRequestKeys = new System.Collections.ArrayList();

		// The list of FETCH keys to expect from the server in return to the request of
		// items listed in fetchRequestKeys.
		System.Collections.ArrayList fetchResponseKeys = new System.Collections.ArrayList();

		// UID of the message for which we received the body structure.
		long uid = 0;

		foreach (Envelope env in envs)
		{
			// Get body structures of all MIME parts as flat list.
			ImapBodyStructureCollection parts = env.BodyStructure.GetAllParts();
			foreach (ImapBodyStructure part in parts)
			{
				if (part.ContentType.ToLower().IndexOf("image/") > -1)
				{
					// For each image, we request its MIME part header and body
					// (the IMAP4 protocol does not support getting the entire MIME
					// part as a single block of data).
					fetchRequestKeys.Add("BODY.PEEK[" + part.PartID + ".HEADER]");
					fetchRequestKeys.Add("BODY.PEEK[" + part.PartID + "]");

					// For BODY.PEEK[...] request the server will return BODY[...] response.
					fetchResponseKeys.Add("BODY[" + part.PartID + ".HEADER]");
					fetchResponseKeys.Add("BODY[" + part.PartID + "]");
				}
			}

			// Second time, we'll get message by its UID. This will avoid the situation
			// when new message arrives between two queries (and thus it could be possible
			// to download wrong message second time).
			uid = env.Uid;
		}

		if (uid > 0)
		{
			// Download images.
			envs = imp.DownloadEnvelopes(uid.ToString(), true,
				EnvelopeParts.Uid, 0, null,
				(string[])fetchRequestKeys.ToArray(typeof(string)));

			foreach (Envelope env in envs)
			{
				for (int i = 0; i < fetchRequestKeys.Count; i += 2)
				{
					// Get MIME part header data.
					byte[] mimePartHeader = (byte[])env.GetEnvelopeItem(
						(string)fetchResponseKeys[i], true);

					// Get MIME part body data.
					byte[] mimePartBody = (byte[])env.GetEnvelopeItem(
						(string)fetchResponseKeys[i + 1], true);

					// Build MIME part data from header data and body data.
					byte[] mimePartData = new byte[mimePartHeader.Length + mimePartBody.Length];
					Buffer.BlockCopy(mimePartHeader, 0, mimePartData, 0, mimePartHeader.Length);
					Buffer.BlockCopy(mimePartBody, 0, mimePartData,
						mimePartHeader.Length, mimePartBody.Length);
					mimePartHeader = null;
					mimePartBody = null;

					// Build Attachment object from the MIME part data.
					MimePart part = MimePart.Parse(mimePartData);
					Attachment picture = new Attachment(part);

					// Save the image into C:\Temp folder.
					picture.SaveToFolder(@"C:\Temp", false);
				}
			}
		}

		imp.Disconnect();
	}
}

using System;
using MailBee;
using MailBee.Pop3Mail;

class Sample
{
	// We set it to true only for the time while DeleteMessages is executing.
	// Alternatively, we could just enable logging only for the time of 
	// DeleteMessages execution and have it disabled at other times.
	private static bool	m_addEntry;

	// LogNewEntry event handler
	private static void OnLogNewEntry(object sender, LogNewEntryEventArgs e)
	{
		// Do not add log records which either do not apply to 
		// DeleteMessages or contain data transferred between the client 
		// and the server. This way, we filter all log messages which do 
		// not directly relate to this sample.
		if (!m_addEntry || e.NewEntry.MessageType != LogMessageType.Info)
		{
			e.NewEntry.AddThisEntry = false;
		}
	}

	// The actual code
	static void Main(string[] args)
	{
		Pop3 pop = new Pop3();

		// Subscribe to the LogNewEntry to filter out unnecessary log records.
		pop.LogNewEntry += new LogNewEntryEventHandler(OnLogNewEntry);

		// Connect to the POP3 server and log in the user account.
		pop.Connect("mail.domain.com");
		pop.Login("jdoe", "secret");

		// Enable logging after already connected to the server.
		// Alternatively, we could move this code above pop.Connect 
		// and set m_addEntry=false there.
		pop.Log.Enabled = true;
		pop.Log.Filename = @"C:\pop3_log.txt";
		pop.Log.Clear();

		// Will add the records produced by DeleteMessages, to the log.
		m_addEntry = true;

		// Flag the messages as deleted using Resources class.
		pop.Log.WriteLine("The following 2 log records are added using standard resources");
		pop.DeleteMessages(1, 3);

		// We do not want to overburden the log with ResetDeletes results.
		m_addEntry = false;
		pop.ResetDeletes();

		// Flag the messages as deleted using MyResources class.
		Resources.Instance = new MyResources();
		m_addEntry = true;
		pop.Log.WriteLine(null);
		pop.Log.WriteLine("The following 2 log records are added using MyResources");
		pop.DeleteMessages(1, 3);

		// We do not want to overburden the log with ResetDeletes and Disconnect results.
		m_addEntry = false;
		pop.ResetDeletes();

		pop.Disconnect();
	}
}

// Derive our resources class from Resource and override the properties 
// which return the string values used to log DeleteMessages method progress.
class MyResources : Resources
{
	public override string Log_Pop3DeletedMessagesStartIndex0Count1
	{
		get { return "{1} message(s) starting at {0} flagged as deleted."; }
	}

	public override string Log_Pop3WillDeleteMessagesStartIndex0Count1
	{
		get { return "Will flag {1} message(s) starting at {0} as deleted."; }
	}
}

// After running this code, C:\pop3_log.txt file should look like below.
[00:41:02.54] [USER] The following 2 log records are added using standard resources
[00:41:02.55] [INFO] Will mark messages (startIndex=1, count=3) as deleted.
[00:41:02.56] [INFO] Messages (startIndex=1, count=3) marked as deleted.
[00:41:02.56] [USER] 
[00:41:02.57] [USER] The following 2 log records are added using MyResources
[00:41:02.57] [INFO] Will flag 3 message(s) starting at 1 as deleted.
[00:41:02.57] [INFO] 3 message(s) starting at 1 flagged as deleted.

